import React from 'react';
import { Loader2 } from 'lucide-react';

// --- Button ---
interface ButtonProps extends React.ButtonHTMLAttributes<HTMLButtonElement> {
  variant?: 'primary' | 'secondary' | 'danger' | 'ghost';
  size?: 'sm' | 'md' | 'lg';
  loading?: boolean;
}

export const Button: React.FC<ButtonProps> = ({ 
  children, variant = 'primary', size = 'md', loading, className = '', ...props 
}) => {
  const baseStyle = "inline-flex items-center justify-center rounded-md font-medium transition-colors focus:outline-none focus:ring-2 focus:ring-offset-2 disabled:opacity-50 disabled:pointer-events-none";

  const variants = {
    primary: "bg-blue-600 text-white hover:bg-blue-700 focus:ring-blue-500",
    secondary: "bg-white text-gray-700 border border-gray-300 hover:bg-gray-50 focus:ring-blue-500",
    danger: "bg-red-600 text-white hover:bg-red-700 focus:ring-red-500",
    ghost: "bg-transparent text-gray-600 hover:bg-gray-100 hover:text-gray-900",
  };

  const sizes = {
    sm: "h-8 px-3 text-xs",
    md: "h-10 px-4 py-2 text-sm",
    lg: "h-12 px-6 text-base",
  };

  return (
    <button 
      className={`${baseStyle} ${variants[variant]} ${sizes[size]} ${className}`} 
      disabled={loading || props.disabled}
      {...props}
    >
      {loading && <Loader2 className="w-4 h-4 mr-2 animate-spin" />}
      {children}
    </button>
  );
};

// --- Badge ---
export const Badge: React.FC<{ type: 'success' | 'warning' | 'danger' | 'neutral', children: React.ReactNode }> = ({ type, children }) => {
  const styles = {
    success: "bg-green-100 text-green-800",
    warning: "bg-yellow-100 text-yellow-800",
    danger: "bg-red-100 text-red-800",
    neutral: "bg-gray-100 text-gray-800",
  };
  return (
    <span className={`inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium ${styles[type]}`}>
      {children}
    </span>
  );
};

// --- Modal ---
interface ModalProps {
  isOpen: boolean;
  onClose: () => void;
  title: string;
  children: React.ReactNode;
  footer?: React.ReactNode;
}

export const Modal: React.FC<ModalProps> = ({ isOpen, onClose, title, children, footer }) => {
  if (!isOpen) return null;
  return (
    <div className="fixed inset-0 z-50 flex items-center justify-center p-4 bg-black/50 backdrop-blur-sm">
      <div className="bg-white rounded-lg shadow-xl w-full max-w-lg transform transition-all">
        <div className="px-6 py-4 border-b border-gray-200 flex justify-between items-center">
          <h3 className="text-lg font-medium text-gray-900">{title}</h3>
          <button onClick={onClose} className="text-gray-400 hover:text-gray-500">✕</button>
        </div>
        <div className="px-6 py-4">{children}</div>
        {footer && (
          <div className="px-6 py-4 border-t border-gray-200 bg-gray-50 flex justify-end gap-2 rounded-b-lg">
            {footer}
          </div>
        )}
      </div>
    </div>
  );
};

// --- ProTable Simulation ---
interface TableProps<T> {
  columns: { title: string; render: (record: T) => React.ReactNode; width?: string }[];
  dataSource: T[];
  rowKey: keyof T;
  loading?: boolean;
}

export const Table = <T extends {}>({ columns, dataSource, rowKey, loading }: TableProps<T>) => {
  if (loading) {
    return <div className="p-8 flex justify-center"><Loader2 className="w-8 h-8 animate-spin text-blue-500" /></div>;
  }

  return (
    <div className="overflow-x-auto border border-gray-200 rounded-lg">
      <table className="min-w-full divide-y divide-gray-200">
        <thead className="bg-gray-50">
          <tr>
            {columns.map((col, idx) => (
              <th key={idx} scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider" style={{ width: col.width }}>
                {col.title}
              </th>
            ))}
          </tr>
        </thead>
        <tbody className="bg-white divide-y divide-gray-200">
          {dataSource.map((record) => (
            <tr key={String(record[rowKey])} className="hover:bg-gray-50 transition-colors">
              {columns.map((col, idx) => (
                <td key={idx} className="px-6 py-4 whitespace-nowrap text-sm text-gray-700">
                  {col.render(record)}
                </td>
              ))}
            </tr>
          ))}
          {dataSource.length === 0 && (
            <tr>
              <td colSpan={columns.length} className="px-6 py-12 text-center text-gray-500">
                No data available
              </td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
};

import React, { useState, useMemo } from 'react';
import { MOCK_INSTITUTIONS, MOCK_REGIONS } from '../constants';
import { Institution, Region } from '../types';
import { Button, Table, Badge, Modal } from './UI';
import { Plus, Trash2, Edit, Network, MapPin } from 'lucide-react';

interface Props {
  currentRegionCode: string;
}

// Tree Item Component for Medical Alliance
const TreeItem = ({ 
  node, 
  allNodes, 
  onSelect 
}: { 
  node: Institution, 
  allNodes: Institution[], 
  onSelect: (inst: Institution) => void 
}) => {
  const children = allNodes.filter(n => n.parentId === node.id);
  const [expanded, setExpanded] = useState(true);

  return (
    <div className="ml-4 border-l border-gray-200 pl-4 relative">
      <div 
        className="flex items-center gap-2 py-2 cursor-pointer hover:text-blue-600 group"
        onClick={(e) => { e.stopPropagation(); onSelect(node); }}
      >
        <span className="w-2 h-2 rounded-full bg-blue-400"></span>
        <span className="text-sm font-medium text-gray-700 group-hover:text-blue-600">{node.name}</span>
        <span className="text-xs text-gray-400 bg-gray-100 px-1 rounded">{node.level}</span>
      </div>
      {expanded && children.length > 0 && (
        <div className="mt-1">
          {children.map(child => (
            <TreeItem key={child.id} node={child} allNodes={allNodes} onSelect={onSelect} />
          ))}
        </div>
      )}
    </div>
  );
};

export const InstitutionManager: React.FC<Props> = ({ currentRegionCode }) => {
  const [selectedInstitution, setSelectedInstitution] = useState<Institution | null>(null);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [loading, setLoading] = useState(false);
  const [viewMode, setViewMode] = useState<'list' | 'tree'>('list');

  // Filter institutions based on drill-down region code (simple prefix match for mock)
  const filteredData = useMemo(() => {
    return MOCK_INSTITUTIONS.filter(inst => inst.regionCode.startsWith(currentRegionCode));
  }, [currentRegionCode]);

  // For the Tree View, find roots (no parent or parent not in filtered set)
  const rootNodes = useMemo(() => {
    const ids = new Set(filteredData.map(d => d.id));
    return filteredData.filter(d => !d.parentId || !ids.has(d.parentId));
  }, [filteredData]);

  const handleDelete = (id: string) => {
    const confirm = window.confirm("Are you sure you want to deactivate this institution? This action requires secondary authorization.");
    if (confirm) {
      setLoading(true);
      setTimeout(() => {
        // Mock API call
        alert("Institution deactivated successfully.");
        setLoading(false);
      }, 1000);
    }
  };

  const handleSave = (e: React.FormEvent) => {
    e.preventDefault();
    setLoading(true);
    setTimeout(() => {
      setIsModalOpen(false);
      setLoading(false);
      alert("Institution saved successfully.");
    }, 800);
  };

  return (
    <div className="space-y-4 h-full flex flex-col">
      {/* Action Bar */}
      <div className="flex justify-between items-center bg-white p-4 rounded-lg shadow-sm border border-gray-200">
        <div className="flex gap-2">
          <Button 
            variant={viewMode === 'list' ? 'primary' : 'secondary'} 
            onClick={() => setViewMode('list')}
            size="sm"
          >
            List View
          </Button>
          <Button 
            variant={viewMode === 'tree' ? 'primary' : 'secondary'} 
            onClick={() => setViewMode('tree')}
            size="sm"
          >
            <Network className="w-4 h-4 mr-2" />
            Alliance Topology
          </Button>
        </div>
        <Button onClick={() => { setSelectedInstitution(null); setIsModalOpen(true); }}>
          <Plus className="w-4 h-4 mr-2" />
          Add Institution
        </Button>
      </div>

      <div className="flex-1 flex gap-4 min-h-0">
        {/* Main Content Area */}
        <div className={`flex-1 bg-white rounded-lg shadow-sm border border-gray-200 overflow-hidden flex flex-col`}>
          {viewMode === 'list' ? (
            <div className="p-4 overflow-auto">
              <Table<Institution>
                rowKey="id"
                dataSource={filteredData}
                columns={[
                  { title: 'Institution Name', render: (r) => <span className="font-medium text-blue-600 cursor-pointer" onClick={() => { setSelectedInstitution(r); setIsModalOpen(true); }}>{r.name}</span> },
                  { title: 'Level', render: (r) => <Badge type="neutral">{r.level}</Badge> },
                  { title: 'Type', render: (r) => r.type },
                  { title: 'Region Code', render: (r) => r.regionCode },
                  { title: 'Status', render: (r) => <Badge type={r.status === 'Active' ? 'success' : 'danger'}>{r.status}</Badge> },
                  { title: 'Actions', render: (r) => (
                    <div className="flex gap-2">
                      <Button size="sm" variant="secondary" onClick={() => { setSelectedInstitution(r); setIsModalOpen(true); }}><Edit className="w-3 h-3" /></Button>
                      <Button size="sm" variant="danger" onClick={() => handleDelete(r.id)}><Trash2 className="w-3 h-3" /></Button>
                    </div>
                  )}
                ]}
              />
            </div>
          ) : (
            <div className="p-8 overflow-auto bg-gray-50/50">
               <h3 className="text-lg font-semibold mb-4 text-gray-800">Medical Alliance Topology</h3>
               <div className="bg-white p-6 rounded-xl border border-gray-200 shadow-sm max-w-3xl">
                 {rootNodes.map(root => (
                   <TreeItem 
                    key={root.id} 
                    node={root} 
                    allNodes={filteredData} 
                    onSelect={(inst) => { setSelectedInstitution(inst); setIsModalOpen(true); }}
                   />
                 ))}
                 {rootNodes.length === 0 && <p className="text-gray-400 italic">No structure found for this region.</p>}
               </div>
            </div>
          )}
        </div>
      </div>

      {/* Add/Edit Modal (Simulated Form) */}
      <Modal 
        isOpen={isModalOpen} 
        onClose={() => setIsModalOpen(false)} 
        title={selectedInstitution ? "Edit Institution Profile" : "Register New Institution"}
        footer={
          <>
            <Button variant="ghost" onClick={() => setIsModalOpen(false)}>Cancel</Button>
            <Button onClick={handleSave} loading={loading}>Confirm Save</Button>
          </>
        }
      >
        <form className="space-y-4" onSubmit={handleSave}>
          <div className="grid grid-cols-2 gap-4">
            <div>
              <label className="block text-sm font-medium text-gray-700">Name</label>
              <input type="text" defaultValue={selectedInstitution?.name} className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500 sm:text-sm border p-2" />
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700">Level</label>
              <select className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500 sm:text-sm border p-2" defaultValue={selectedInstitution?.level}>
                <option>Grade 3A</option>
                <option>Grade 3B</option>
                <option>Grade 2A</option>
                <option>Unrated</option>
              </select>
            </div>
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700">Region (Cascading Simulation)</label>
            <div className="flex gap-2 mt-1">
              <select className="block w-1/3 rounded-md border-gray-300 shadow-sm border p-2 bg-gray-50" disabled>
                <option>Zhejiang</option>
              </select>
              <select className="block w-1/3 rounded-md border-gray-300 shadow-sm border p-2">
                <option>Hangzhou</option>
                <option>Ningbo</option>
              </select>
              <select className="block w-1/3 rounded-md border-gray-300 shadow-sm border p-2">
                <option>West Lake Dist.</option>
                <option>Binjiang Dist.</option>
              </select>
            </div>
          </div>

          <div>
             <label className="block text-sm font-medium text-gray-700">Geo Coordinates</label>
             <div className="relative rounded-md shadow-sm mt-1">
                <div className="pointer-events-none absolute inset-y-0 left-0 flex items-center pl-3">
                  <MapPin className="h-4 w-4 text-gray-400" />
                </div>
                <input type="text" className="block w-full rounded-md border-gray-300 pl-10 focus:border-blue-500 focus:ring-blue-500 sm:text-sm border p-2" placeholder="120.1551, 30.2741" />
             </div>
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700">Alliance Parent Node (Upsream)</label>
             <select className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500 sm:text-sm border p-2" defaultValue={selectedInstitution?.parentId || ''}>
                <option value="">-- None (Root Node) --</option>
                {MOCK_INSTITUTIONS.filter(i => i.id !== selectedInstitution?.id).map(i => (
                  <option key={i.id} value={i.id}>{i.name}</option>
                ))}
              </select>
          </div>
        </form>
      </Modal>
    </div>
  );
};

import React, { useMemo } from 'react';
import { MOCK_TRANSACTIONS } from '../constants';
import { Transaction } from '../types';
import { Table, Badge, Button } from './UI';
import { Eye, FileText, Download } from 'lucide-react';

interface Props {
  currentRegionCode: string; // Filter by region if we had region data in transactions
}

export const FinancialScan: React.FC<Props> = () => {
  // Logic: Highlight if Amount > StandardPrice * 1.2
  const isAbnormal = (record: Transaction) => {
    return record.amount > record.standardPrice * 1.2;
  };

  const dataSource = MOCK_TRANSACTIONS.map(t => ({
    ...t,
    deviation: ((t.amount - t.standardPrice) / t.standardPrice * 100).toFixed(1)
  }));

  const columns = [
    { title: 'Trans ID', render: (r: any) => <span className="font-mono text-xs">{r.id}</span> },
    { title: 'Service', render: (r: any) => r.serviceName },
    { title: 'Institution', render: (r: any) => r.institutionName },
    { 
      title: 'Amount (¥)', 
      render: (r: any) => (
        <div className="flex flex-col">
            <span className={isAbnormal(r) ? "text-red-600 font-bold" : "text-gray-900"}>
                ¥{r.amount.toFixed(2)}
            </span>
            <span className="text-xs text-gray-400">Std: ¥{r.standardPrice}</span>
        </div>
      ) 
    },
    { 
      title: 'Compliance', 
      render: (r: any) => {
        const abnormal = isAbnormal(r);
        return (
            <Badge type={abnormal ? 'danger' : 'success'}>
                {abnormal ? `Abnormal (+${r.deviation}%)` : 'Normal'}
            </Badge>
        )
      } 
    },
    { title: 'Date', render: (r: any) => r.date },
    { 
      title: 'Action', 
      render: (r: any) => (
          <Button size="sm" variant="secondary" onClick={() => alert(`Drill down to Invoice #${r.id} for ${r.patientName}`)}>
              <FileText className="w-3 h-3 mr-1" /> Audit
          </Button>
      )
    }
  ];

  return (
    <div className="space-y-4">
      <div className="bg-white p-4 rounded-lg shadow-sm border border-gray-200 flex justify-between items-center">
        <div>
            <h2 className="text-lg font-bold text-gray-800">Financial Compliance Scan</h2>
            <p className="text-sm text-gray-500">Automatically highlighting transactions deviation > 20% from standard pricing.</p>
        </div>
        <Button variant="secondary">
            <Download className="w-4 h-4 mr-2" /> Export Report
        </Button>
      </div>

      <div className="bg-white rounded-lg shadow-sm border border-gray-200">
        <Table<any>
            rowKey="id"
            dataSource={dataSource}
            columns={columns}
        />
      </div>
    </div>
  );
};

