  );
};

import React, { useState, useEffect, useMemo } from 'react';
import { 
  LayoutDashboard, Building2, AlertTriangle, FileCheck, ScanLine, 
  ChevronDown, Bell, LogOut, Map, Search, User as UserIcon
} from 'lucide-react';
import { AdminLevel, Region, User } from './types';
import { MOCK_USERS, MOCK_REGIONS, ROUTE_CONFIG } from './constants';
import { InstitutionManager } from './components/InstitutionManager';
import { WarningCenter } from './components/WarningCenter';
import { FinancialScan } from './components/FinancialScan';
import { Button } from './components/UI';

// --- Global Region Context Logic (Drill-down State) ---
// In a real app, this would be a React Context. 
// Here we manage it in App component for simplicity within 1 file limit constraints.

export default function App() {
  // 1. Auth State (Simulated)
  const [currentUser, setCurrentUser] = useState<User>(MOCK_USERS.provinceAdmin);

  // 2. Navigation State
  const [currentRoute, setCurrentRoute] = useState<string>('/dashboard');

  // 3. Drill-down State (The "Global Region Filter")
  // Defaults to user's own region. Can traverse down, but not up beyond user's scope.
  const [selectedRegionCode, setSelectedRegionCode] = useState<string>(currentUser.regionCode);

  useEffect(() => {
    // Reset selection when user changes (login simulation)
    setSelectedRegionCode(currentUser.regionCode);
    setCurrentRoute('/dashboard');
  }, [currentUser]);

  // Find the actual Region object for the selected code
  const activeRegion = useMemo(() => 
    MOCK_REGIONS.find(r => r.code === selectedRegionCode) || MOCK_REGIONS[0], 
    [selectedRegionCode]
  );

  // Available drill-down options (Children of current selection)
  const subRegions = useMemo(() => 
    MOCK_REGIONS.filter(r => r.parentCode === activeRegion.code),
    [activeRegion]
  );

  // Available navigation items based on User Permission
  const menuItems = useMemo(() => 
    ROUTE_CONFIG.filter(route => 
      currentUser.adminLevel >= route.minLevel && 
      (!route.maxLevel || currentUser.adminLevel <= route.maxLevel)
    ),
    [currentUser]
  );

  // --- Render Components ---

  const renderContent = () => {
    switch(currentRoute) {
      case '/institution':
        return <InstitutionManager currentRegionCode={selectedRegionCode} />;
      case '/warning':
        return <WarningCenter />;
      case '/finance':
        return <FinancialScan currentRegionCode={selectedRegionCode} />;
      case '/dashboard':
      default:
        return (
          <div className="p-8 text-center text-gray-500">
            <h2 className="text-2xl font-bold text-gray-700 mb-4">Welcome, {currentUser.name}</h2>
            <p>You are viewing data for: <span className="font-bold text-blue-600">{activeRegion.name}</span></p>
            <div className="mt-8 grid grid-cols-1 md:grid-cols-3 gap-6 text-left">
                <div className="bg-white p-6 rounded-lg shadow-sm border border-gray-200">
                    <div className="text-sm text-gray-500">Total Institutions</div>
                    <div className="text-3xl font-bold text-gray-900 mt-2">1,240</div>
                </div>
                <div className="bg-white p-6 rounded-lg shadow-sm border border-gray-200">
                    <div className="text-sm text-gray-500">Tele-consultations (This Month)</div>
                    <div className="text-3xl font-bold text-blue-600 mt-2">8,502</div>
                </div>
                <div className="bg-white p-6 rounded-lg shadow-sm border border-gray-200">
                    <div className="text-sm text-gray-500">Compliance Rate</div>
                    <div className="text-3xl font-bold text-green-600 mt-2">98.2%</div>
                </div>
            </div>
          </div>
        );
    }
  };

  return (
    <div className="flex h-screen bg-gray-50 font-sans text-slate-900">

      {/* Sidebar - Dark Theme #1F2A38 */}
      <aside className="w-64 bg-[#1F2A38] flex flex-col shadow-lg z-20 flex-shrink-0">
        <div className="h-16 flex items-center px-6 bg-[#17212e]">
          <div className="w-8 h-8 bg-blue-500 rounded-lg flex items-center justify-center mr-3 text-white font-bold">T</div>
          <span className="text-white font-semibold tracking-wide text-sm">TeleMed Admin</span>
        </div>

        <nav className="flex-1 py-6 px-3 space-y-1">
          {menuItems.map(item => {
            const Icon = {
              LayoutDashboard, Building2, AlertTriangle, FileCheck, ScanLine
            }[item.icon] || LayoutDashboard;

            const isActive = currentRoute === item.path;

            return (
              <button
                key={item.key}
                onClick={() => setCurrentRoute(item.path)}
                className={`w-full flex items-center px-3 py-2.5 text-sm font-medium rounded-md transition-all duration-200 group
                  ${isActive 
                    ? 'bg-blue-600 text-white shadow-md' 
                    : 'text-gray-400 hover:bg-[#2A3B4D] hover:text-white'
                  }`}
              >
                <Icon className={`mr-3 h-5 w-5 ${isActive ? 'text-white' : 'text-gray-500 group-hover:text-white'}`} />
                {item.label}
              </button>
            );
          })}
        </nav>

        {/* User Profile / Role Switcher for Demo */}
        <div className="p-4 bg-[#17212e] border-t border-gray-700">
          <div className="flex items-center gap-3 mb-4">
            <div className="w-8 h-8 rounded-full bg-gray-500 flex items-center justify-center text-xs text-white">
              {currentUser.name[0]}
            </div>
            <div className="flex-1 min-w-0">
              <p className="text-sm font-medium text-white truncate">{currentUser.name}</p>
              <p className="text-xs text-gray-400 truncate">{currentUser.role}</p>
            </div>
          </div>
          <div className="grid grid-cols-3 gap-1">
             <button onClick={() => setCurrentUser(MOCK_USERS.provinceAdmin)} className="text-[10px] bg-gray-700 text-gray-300 p-1 rounded hover:bg-gray-600" title="Switch to Province">PROV</button>
             <button onClick={() => setCurrentUser(MOCK_USERS.cityAdmin)} className="text-[10px] bg-gray-700 text-gray-300 p-1 rounded hover:bg-gray-600" title="Switch to City">CITY</button>
             <button onClick={() => setCurrentUser(MOCK_USERS.countyAdmin)} className="text-[10px] bg-gray-700 text-gray-300 p-1 rounded hover:bg-gray-600" title="Switch to County">CNTY</button>
          </div>
        </div>
      </aside>

      {/* Main Content */}
      <div className="flex-1 flex flex-col min-w-0 overflow-hidden">

        {/* Header - Global Region Filter */}
        <header className="bg-white shadow-sm h-16 flex items-center justify-between px-8 z-10 border-b border-gray-200">
          <div className="flex items-center gap-4">
            <h1 className="text-lg font-bold text-gray-800 tracking-tight">
               {menuItems.find(m => m.path === currentRoute)?.label}
            </h1>

            {/* The Administrative Division Filter */}
            <div className="ml-8 flex items-center gap-2 bg-gray-100 rounded-full px-4 py-1.5 border border-gray-200">
               <Map className="w-4 h-4 text-gray-500" />
               <span className="text-sm font-medium text-gray-700">{activeRegion.name}</span>

               {/* Only show drill down if subregions exist */}
               {subRegions.length > 0 && (
                 <>
                   <span className="text-gray-300">|</span>
                   <select 
                      className="bg-transparent text-sm text-gray-600 focus:outline-none cursor-pointer hover:text-blue-600"
                      onChange={(e) => {
                        if (e.target.value) setSelectedRegionCode(e.target.value);
                      }}
                      value=""
                   >
                     <option value="" disabled>Drill down...</option>
                     {subRegions.map(r => (
                       <option key={r.code} value={r.code}>{r.name}</option>
                     ))}
                   </select>
                 </>
               )}

               {/* "Reset to Top" button if drilled down */}
               {activeRegion.code !== currentUser.regionCode && (
                 <button 
                   onClick={() => setSelectedRegionCode(currentUser.regionCode)}
                   className="ml-2 text-xs text-blue-600 hover:underline"
                 >
                   Reset
                 </button>
               )}
            </div>
          </div>

          <div className="flex items-center gap-4">
             <div className="relative">
                <Bell className="w-5 h-5 text-gray-500 cursor-pointer hover:text-gray-700" />
                <span className="absolute -top-1 -right-1 w-2 h-2 bg-red-500 rounded-full"></span>
             </div>
             <Button variant="ghost" size="sm">
                <LogOut className="w-4 h-4 mr-2" />
                Logout
             </Button>
          </div>
        </header>

        {/* Page Content */}
        <main className="flex-1 overflow-y-auto p-6 scroll-smooth">
          {renderContent()}
        </main>
      </div>
    </div>
  );
}

import React, { useState, useMemo } from 'react';
import { MOCK_INSTITUTIONS, MOCK_REGIONS } from '../constants';
import { Institution, Region } from '../types';
import { Button, Table, Badge, Modal } from './UI';
import { Plus, Trash2, Edit, Network, MapPin } from 'lucide-react';

interface Props {
  currentRegionCode: string;
}

interface TreeItemProps {
  node: Institution;
  allNodes: Institution[];
  onSelect: (inst: Institution) => void;
}

// Tree Item Component for Medical Alliance
const TreeItem: React.FC<TreeItemProps> = ({ 
  node, 
  allNodes, 
  onSelect 
}) => {
  const children = allNodes.filter(n => n.parentId === node.id);
  const [expanded, setExpanded] = useState(true);

  return (
    <div className="ml-4 border-l border-gray-200 pl-4 relative">
      <div 
        className="flex items-center gap-2 py-2 cursor-pointer hover:text-blue-600 group"
        onClick={(e) => { e.stopPropagation(); onSelect(node); }}
      >
        <span className="w-2 h-2 rounded-full bg-blue-400"></span>
        <span className="text-sm font-medium text-gray-700 group-hover:text-blue-600">{node.name}</span>
        <span className="text-xs text-gray-400 bg-gray-100 px-1 rounded">{node.level}</span>
      </div>
      {expanded && children.length > 0 && (
        <div className="mt-1">
          {children.map(child => (
            <TreeItem key={child.id} node={child} allNodes={allNodes} onSelect={onSelect} />
          ))}
        </div>
      )}
    </div>
  );
};

export const InstitutionManager: React.FC<Props> = ({ currentRegionCode }) => {
  const [selectedInstitution, setSelectedInstitution] = useState<Institution | null>(null);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [loading, setLoading] = useState(false);
  const [viewMode, setViewMode] = useState<'list' | 'tree'>('list');

  // Filter institutions based on drill-down region code (simple prefix match for mock)
  const filteredData = useMemo(() => {
    return MOCK_INSTITUTIONS.filter(inst => inst.regionCode.startsWith(currentRegionCode));
  }, [currentRegionCode]);

  // For the Tree View, find roots (no parent or parent not in filtered set)
  const rootNodes = useMemo(() => {
    const ids = new Set(filteredData.map(d => d.id));
    return filteredData.filter(d => !d.parentId || !ids.has(d.parentId));
  }, [filteredData]);

  const handleDelete = (id: string) => {
    const confirm = window.confirm("Are you sure you want to deactivate this institution? This action requires secondary authorization.");
    if (confirm) {
      setLoading(true);
      setTimeout(() => {
        // Mock API call
        alert("Institution deactivated successfully.");
        setLoading(false);
      }, 1000);
    }
  };

  const handleSave = (e: React.FormEvent) => {
    e.preventDefault();
    setLoading(true);
    setTimeout(() => {
      setIsModalOpen(false);
      setLoading(false);
      alert("Institution saved successfully.");
    }, 800);
  };

  return (
    <div className="space-y-4 h-full flex flex-col">
      {/* Action Bar */}
      <div className="flex justify-between items-center bg-white p-4 rounded-lg shadow-sm border border-gray-200">
        <div className="flex gap-2">
          <Button 
            variant={viewMode === 'list' ? 'primary' : 'secondary'} 
            onClick={() => setViewMode('list')}
            size="sm"
          >
            List View
          </Button>
          <Button 
            variant={viewMode === 'tree' ? 'primary' : 'secondary'} 
            onClick={() => setViewMode('tree')}
            size="sm"
          >
            <Network className="w-4 h-4 mr-2" />
            Alliance Topology
          </Button>
        </div>
        <Button onClick={() => { setSelectedInstitution(null); setIsModalOpen(true); }}>
          <Plus className="w-4 h-4 mr-2" />
          Add Institution
        </Button>
      </div>

      <div className="flex-1 flex gap-4 min-h-0">
        {/* Main Content Area */}
        <div className={`flex-1 bg-white rounded-lg shadow-sm border border-gray-200 overflow-hidden flex flex-col`}>
          {viewMode === 'list' ? (
            <div className="p-4 overflow-auto">
              <Table<Institution>
                rowKey="id"
                dataSource={filteredData}
                columns={[
                  { title: 'Institution Name', render: (r) => <span className="font-medium text-blue-600 cursor-pointer" onClick={() => { setSelectedInstitution(r); setIsModalOpen(true); }}>{r.name}</span> },
                  { title: 'Level', render: (r) => <Badge type="neutral">{r.level}</Badge> },
                  { title: 'Type', render: (r) => r.type },
                  { title: 'Region Code', render: (r) => r.regionCode },
                  { title: 'Status', render: (r) => <Badge type={r.status === 'Active' ? 'success' : 'danger'}>{r.status}</Badge> },
                  { title: 'Actions', render: (r) => (
                    <div className="flex gap-2">
                      <Button size="sm" variant="secondary" onClick={() => { setSelectedInstitution(r); setIsModalOpen(true); }}><Edit className="w-3 h-3" /></Button>
                      <Button size="sm" variant="danger" onClick={() => handleDelete(r.id)}><Trash2 className="w-3 h-3" /></Button>
                    </div>
                  )}
                ]}
              />
            </div>
          ) : (
            <div className="p-8 overflow-auto bg-gray-50/50">
               <h3 className="text-lg font-semibold mb-4 text-gray-800">Medical Alliance Topology</h3>
               <div className="bg-white p-6 rounded-xl border border-gray-200 shadow-sm max-w-3xl">
                 {rootNodes.map(root => (
                   <TreeItem 
                    key={root.id} 
                    node={root} 
                    allNodes={filteredData} 
                    onSelect={(inst) => { setSelectedInstitution(inst); setIsModalOpen(true); }}
                   />
                 ))}
                 {rootNodes.length === 0 && <p className="text-gray-400 italic">No structure found for this region.</p>}
               </div>
            </div>
          )}
        </div>
      </div>

      {/* Add/Edit Modal (Simulated Form) */}
      <Modal 
        isOpen={isModalOpen} 
        onClose={() => setIsModalOpen(false)} 
        title={selectedInstitution ? "Edit Institution Profile" : "Register New Institution"}
        footer={
          <>
            <Button variant="ghost" onClick={() => setIsModalOpen(false)}>Cancel</Button>
            <Button onClick={handleSave} loading={loading}>Confirm Save</Button>
          </>
        }
      >
        <form className="space-y-4" onSubmit={handleSave}>
          <div className="grid grid-cols-2 gap-4">
            <div>
              <label className="block text-sm font-medium text-gray-700">Name</label>
              <input type="text" defaultValue={selectedInstitution?.name} className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500 sm:text-sm border p-2" />
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700">Level</label>
              <select className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500 sm:text-sm border p-2" defaultValue={selectedInstitution?.level}>
                <option>Grade 3A</option>
                <option>Grade 3B</option>
                <option>Grade 2A</option>
                <option>Unrated</option>
              </select>
            </div>
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700">Region (Cascading Simulation)</label>
            <div className="flex gap-2 mt-1">
              <select className="block w-1/3 rounded-md border-gray-300 shadow-sm border p-2 bg-gray-50" disabled>
                <option>Zhejiang</option>
              </select>
              <select className="block w-1/3 rounded-md border-gray-300 shadow-sm border p-2">
                <option>Hangzhou</option>
                <option>Ningbo</option>
              </select>
              <select className="block w-1/3 rounded-md border-gray-300 shadow-sm border p-2">
                <option>West Lake Dist.</option>
                <option>Binjiang Dist.</option>
              </select>
            </div>
          </div>

          <div>
             <label className="block text-sm font-medium text-gray-700">Geo Coordinates</label>
             <div className="relative rounded-md shadow-sm mt-1">
                <div className="pointer-events-none absolute inset-y-0 left-0 flex items-center pl-3">
                  <MapPin className="h-4 w-4 text-gray-400" />
                </div>
                <input type="text" className="block w-full rounded-md border-gray-300 pl-10 focus:border-blue-500 focus:ring-blue-500 sm:text-sm border p-2" placeholder="120.1551, 30.2741" />
             </div>
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700">Alliance Parent Node (Upsream)</label>
             <select className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500 sm:text-sm border p-2" defaultValue={selectedInstitution?.parentId || ''}>
                <option value="">-- None (Root Node) --</option>
                {MOCK_INSTITUTIONS.filter(i => i.id !== selectedInstitution?.id).map(i => (
                  <option key={i.id} value={i.id}>{i.name}</option>
                ))}
              </select>
          </div>
        </form>
      </Modal>
    </div>
  );
};

