interface Props {
  currentRegionCode: string; // Filter by region if we had region data in transactions
}

export const FinancialScan: React.FC<Props> = () => {
  // Logic: Highlight if Amount > StandardPrice * 1.2
  const isAbnormal = (record: Transaction) => {
    return record.amount > record.standardPrice * 1.2;
  };

  const dataSource = MOCK_TRANSACTIONS.map(t => ({
    ...t,
    deviation: ((t.amount - t.standardPrice) / t.standardPrice * 100).toFixed(1)
  }));

  const columns = [
    { title: '交易流水号', render: (r: any) => <span className="font-mono text-xs">{r.id}</span> },
    { title: '服务项目', render: (r: any) => r.serviceName },
    { title: '执行机构', render: (r: any) => r.institutionName },
    { 
      title: '金额 (¥)', 
      render: (r: any) => (
        <div className="flex flex-col">
            <span className={isAbnormal(r) ? "text-red-600 font-bold" : "text-gray-900"}>
                ¥{r.amount.toFixed(2)}
            </span>
            <span className="text-xs text-gray-400">标准价: ¥{r.standardPrice}</span>
        </div>
      ) 
    },
    { 
      title: '合规性', 
      render: (r: any) => {
        const abnormal = isAbnormal(r);
        return (
            <Badge type={abnormal ? 'danger' : 'success'}>
                {abnormal ? `异常 (+${r.deviation}%)` : '正常'}
            </Badge>
        )
      } 
    },
    { title: '日期', render: (r: any) => r.date },
    { 
      title: '操作', 
      render: (r: any) => (
          <Button size="sm" variant="secondary" onClick={() => alert(`正在穿透审计患者 [${r.patientName}] 的 #${r.id} 号单据...`)}>
              <FileText className="w-3 h-3 mr-1" /> 审计
          </Button>
      )
    }
  ];

  return (
    <div className="space-y-4">
      <div className="bg-white p-4 rounded-lg shadow-sm border border-gray-200 flex justify-between items-center">
        <div>
            <h2 className="text-lg font-bold text-gray-800">财务合规扫描</h2>
            <p className="text-sm text-gray-500">自动高亮偏离标准定价 > 20% 的异常交易。</p>
        </div>
        <Button variant="secondary">
            <Download className="w-4 h-4 mr-2" /> 导出报告
        </Button>
      </div>

      <div className="bg-white rounded-lg shadow-sm border border-gray-200">
        <Table<any>
            rowKey="id"
            dataSource={dataSource}
            columns={columns}
        />
      </div>
    </div>
  );
};

