import React, { useState, useEffect, useMemo } from 'react';
import { 
  LayoutDashboard, Building2, AlertTriangle, FileCheck, ScanLine, 
  ChevronDown, Bell, LogOut, Map, Search, User as UserIcon,
  Database, ScrollText, Activity, BadgeCheck, Scale, Settings, CheckSquare
} from 'lucide-react';
import { AdminLevel, Region, User } from './types';
import { MOCK_USERS, MOCK_REGIONS, ROUTE_CONFIG } from './constants';
import { InstitutionManager } from './components/InstitutionManager';
import { WarningCenter } from './components/WarningCenter';
import { FinancialScan } from './components/FinancialScan';
import { Button } from './components/UI';

// --- Global Region Context Logic (Drill-down State) ---
export default function App() {
  // 1. Auth State (Simulated)
  const [currentUser, setCurrentUser] = useState<User>(MOCK_USERS.provinceAdmin);

  // 2. Navigation State
  const [currentRoute, setCurrentRoute] = useState<string>('/dashboard');

  // 3. Drill-down State (The "Global Region Filter")
  const [selectedRegionCode, setSelectedRegionCode] = useState<string>(currentUser.regionCode);

  useEffect(() => {
    // Reset selection when user changes (login simulation)
    setSelectedRegionCode(currentUser.regionCode);
    setCurrentRoute('/dashboard');
  }, [currentUser]);

  // Find the actual Region object for the selected code
  const activeRegion = useMemo(() => 
    MOCK_REGIONS.find(r => r.code === selectedRegionCode) || MOCK_REGIONS[0], 
    [selectedRegionCode]
  );

  // Available drill-down options (Children of current selection)
  const subRegions = useMemo(() => 
    MOCK_REGIONS.filter(r => r.parentCode === activeRegion.code),
    [activeRegion]
  );

  // Available navigation items based on User Permission
  const menuItems = useMemo(() => 
    ROUTE_CONFIG.filter(route => 
      currentUser.adminLevel >= route.minLevel && 
      (!route.maxLevel || currentUser.adminLevel <= route.maxLevel)
    ),
    [currentUser]
  );

  // Generic Placeholder for unimplemented modules, displaying detailed feature list
  const GenericModule = ({ title, icon: Icon, features = [] }: { title: string, icon: any, features?: string[] }) => (
    <div className="h-full flex flex-col items-center justify-center p-8 bg-white rounded-lg border border-dashed border-gray-300">
      <div className="bg-blue-50 p-6 rounded-full mb-6">
        <Icon className="w-16 h-16 text-blue-500" />
      </div>
      <h2 className="text-2xl font-bold text-gray-800 mb-2">{title}</h2>
      <p className="text-gray-500 mb-8">该核心模块正在建设中，包含以下关键功能：</p>

      <div className="grid grid-cols-1 md:grid-cols-2 gap-4 max-w-2xl w-full">
        {features.map((feature, idx) => (
          <div key={idx} className="flex items-start gap-3 p-4 bg-gray-50 rounded-lg border border-gray-100">
            <CheckSquare className="w-5 h-5 text-green-600 shrink-0 mt-0.5" />
            <span className="text-gray-700 font-medium">{feature}</span>
          </div>
        ))}
      </div>

      <div className="mt-10 flex gap-4">
        <Button variant="secondary">查看详细设计文档</Button>
        <Button variant="primary">联系管理员</Button>
      </div>
    </div>
  );

  const Dashboard = () => (
    <div className="space-y-6">
       <div className="p-8 text-center text-gray-500 bg-white rounded-lg shadow-sm border border-gray-200">
          <h2 className="text-2xl font-bold text-gray-700 mb-4">欢迎您，{currentUser.name}</h2>
          <p>您当前监管的行政区域为：<span className="font-bold text-blue-600">{activeRegion.name}</span></p>
          <div className="mt-8 grid grid-cols-1 md:grid-cols-3 gap-6 text-left">
              <div className="bg-blue-50 p-6 rounded-lg border border-blue-100 relative overflow-hidden">
                  <div className="text-sm text-blue-600 font-medium">入网机构覆盖率</div>
                  <div className="text-3xl font-bold text-blue-900 mt-2">100%</div>
                  <Building2 className="absolute right-4 bottom-4 text-blue-200 w-12 h-12" />
              </div>
              <div className="bg-indigo-50 p-6 rounded-lg border border-indigo-100 relative overflow-hidden">
                  <div className="text-sm text-indigo-600 font-medium">本月远程会诊人次</div>
                  <div className="text-3xl font-bold text-indigo-900 mt-2">8,502</div>
                  <Activity className="absolute right-4 bottom-4 text-indigo-200 w-12 h-12" />
              </div>
              <div className="bg-emerald-50 p-6 rounded-lg border border-emerald-100 relative overflow-hidden">
                  <div className="text-sm text-emerald-600 font-medium">业务合规率</div>
                  <div className="text-3xl font-bold text-emerald-900 mt-2">98.2%</div>
                  <FileCheck className="absolute right-4 bottom-4 text-emerald-200 w-12 h-12" />
              </div>
          </div>
        </div>

        {/* Quick Access Grid based on new Modules */}
        <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
           {menuItems.slice(1, 8).map(item => {
             const Icon = {
                Building2, Database, ScrollText, Activity, BadgeCheck, Scale, Settings
             }[item.icon] || LayoutDashboard;

             return (
               <button 
                key={item.key}
                onClick={() => setCurrentRoute(item.path)}
                className="flex flex-col gap-2 p-4 bg-white rounded-lg border border-gray-200 hover:shadow-md transition-shadow text-left h-full"
               >
                 <div className="flex items-center gap-3">
                    <div className="p-2 bg-gray-100 rounded-lg group-hover:bg-blue-100">
                        <Icon className="w-5 h-5 text-gray-600" />
                    </div>
                    <span className="font-bold text-gray-700">{item.label}</span>
                 </div>
                 <div className="mt-2 pl-1 space-y-1">
                    {item.features?.slice(0, 2).map((f, i) => (
                        <p key={i} className="text-xs text-gray-500 truncate">• {f}</p>
                    ))}
                 </div>
               </button>
             )
           })}
        </div>
    </div>
  );

  // --- Render Components ---

  const renderContent = () => {
    // Helper to find config for passing features
    const getConfig = (key: string) => ROUTE_CONFIG.find(r => r.key === key);

    switch(currentRoute) {
      case '/institution':
        // Module 1: Institution Monitoring
        return <InstitutionManager currentRegionCode={selectedRegionCode} />;

      case '/resources':
        // Module 2: Resource Monitoring
        return <GenericModule title="核心资源监管" icon={Database} features={getConfig('resources')?.features} />;

      case '/services':
        // Module 3: Service Monitoring
        return <GenericModule title="服务目录监管" icon={ScrollText} features={getConfig('services')?.features} />;

      case '/operations':
        // Module 4: Operation Monitoring
        return <GenericModule title="运行数据监测" icon={Activity} features={getConfig('operations')?.features} />;

      case '/quality':
        // Module 5: Quality Monitoring (Contains the Warning Center logic)
        return <WarningCenter />;

      case '/finance':
        // Module 6: Financial Monitoring
        return <FinancialScan currentRegionCode={selectedRegionCode} />;

      case '/system':
        // Public System Functions
        return <GenericModule title="系统运维中心" icon={Settings} features={getConfig('system')?.features} />;

      case '/dashboard':
      default:
        return <Dashboard />;
    }
  };

  return (
    <div className="flex h-screen bg-gray-50 font-sans text-slate-900">

      {/* Sidebar - Dark Theme #1F2A38 */}
      <aside className="w-64 bg-[#1F2A38] flex flex-col shadow-lg z-20 flex-shrink-0 transition-all duration-300">
        <div className="h-16 flex items-center px-6 bg-[#17212e]">
          <div className="w-8 h-8 bg-blue-500 rounded-lg flex items-center justify-center mr-3 text-white font-bold shrink-0">医</div>
          <span className="text-white font-semibold tracking-wide text-sm truncate">远程医疗监管平台</span>
        </div>

        <nav className="flex-1 py-6 px-3 space-y-1 overflow-y-auto">
          {menuItems.map(item => {
            const Icon = {
              LayoutDashboard, Building2, Database, ScrollText, Activity, BadgeCheck, Scale, Settings
            }[item.icon] || LayoutDashboard;

            const isActive = currentRoute === item.path;

            return (
              <button
                key={item.key}
                onClick={() => setCurrentRoute(item.path)}
                className={`w-full flex items-center px-3 py-2.5 text-sm font-medium rounded-md transition-all duration-200 group
                  ${isActive 
                    ? 'bg-blue-600 text-white shadow-md' 
                    : 'text-gray-400 hover:bg-[#2A3B4D] hover:text-white'
                  }`}
              >
                <Icon className={`mr-3 h-5 w-5 shrink-0 ${isActive ? 'text-white' : 'text-gray-500 group-hover:text-white'}`} />
                <span className="truncate">{item.label}</span>
              </button>
            );
          })}
        </nav>

        {/* User Profile / Role Switcher for Demo */}
        <div className="p-4 bg-[#17212e] border-t border-gray-700">
          <div className="flex items-center gap-3 mb-4">
            <div className="w-8 h-8 rounded-full bg-gray-500 flex items-center justify-center text-xs text-white shrink-0">
              {currentUser.name[0]}
            </div>
            <div className="flex-1 min-w-0">
              <p className="text-sm font-medium text-white truncate">{currentUser.name}</p>
              <p className="text-xs text-gray-400 truncate">{currentUser.role}</p>
            </div>
          </div>
          <div className="grid grid-cols-3 gap-1">
             <button onClick={() => setCurrentUser(MOCK_USERS.provinceAdmin)} className="text-[10px] bg-gray-700 text-gray-300 p-1 rounded hover:bg-gray-600 transition-colors" title="切换至省级视角">省级</button>
             <button onClick={() => setCurrentUser(MOCK_USERS.cityAdmin)} className="text-[10px] bg-gray-700 text-gray-300 p-1 rounded hover:bg-gray-600 transition-colors" title="切换至市级视角">市级</button>
             <button onClick={() => setCurrentUser(MOCK_USERS.countyAdmin)} className="text-[10px] bg-gray-700 text-gray-300 p-1 rounded hover:bg-gray-600 transition-colors" title="切换至县级视角">县级</button>
          </div>
        </div>
      </aside>

      {/* Main Content */}
      <div className="flex-1 flex flex-col min-w-0 overflow-hidden">

        {/* Header - Global Region Filter */}
        <header className="bg-white shadow-sm h-16 flex items-center justify-between px-8 z-10 border-b border-gray-200 shrink-0">
          <div className="flex items-center gap-4 flex-1 min-w-0">
            <h1 className="text-lg font-bold text-gray-800 tracking-tight truncate">
               {menuItems.find(m => m.path === currentRoute)?.label}
            </h1>

            {/* The Administrative Division Filter */}
            <div className="hidden md:flex ml-8 items-center gap-2 bg-gray-100 rounded-full px-4 py-1.5 border border-gray-200">
               <Map className="w-4 h-4 text-gray-500" />
               <span className="text-sm font-medium text-gray-700 whitespace-nowrap">{activeRegion.name}</span>

               {/* Only show drill down if subregions exist */}
               {subRegions.length > 0 && (
                 <>
                   <span className="text-gray-300">|</span>
                   <select 
                      className="bg-transparent text-sm text-gray-600 focus:outline-none cursor-pointer hover:text-blue-600 max-w-[120px]"
                      onChange={(e) => {
                        if (e.target.value) setSelectedRegionCode(e.target.value);
                      }}
                      value=""
                   >
                     <option value="" disabled>下钻区域...</option>
                     {subRegions.map(r => (
                       <option key={r.code} value={r.code}>{r.name}</option>
                     ))}
                   </select>
                 </>
               )}

               {/* "Reset to Top" button if drilled down */}
               {activeRegion.code !== currentUser.regionCode && (
                 <button 
                   onClick={() => setSelectedRegionCode(currentUser.regionCode)}
                   className="ml-2 text-xs text-blue-600 hover:underline whitespace-nowrap"
                 >
                   重置
                 </button>
               )}
            </div>
          </div>

          <div className="flex items-center gap-4 shrink-0">
             <div className="relative">
                <Bell className="w-5 h-5 text-gray-500 cursor-pointer hover:text-gray-700" />
                <span className="absolute -top-1 -right-1 w-2 h-2 bg-red-500 rounded-full"></span>
             </div>
             <div className="h-6 w-px bg-gray-200"></div>
             <Button variant="ghost" size="sm">
                <LogOut className="w-4 h-4 mr-2" />
                退出登录
             </Button>
          </div>
        </header>

        {/* Page Content */}
        <main className="flex-1 overflow-y-auto p-6 scroll-smooth">
          <div className="max-w-7xl mx-auto h-full">
            {renderContent()}
          </div>
        </main>
      </div>
    </div>
  );
}

