// Updated Tab Keys based on requirements
type TabType = 'info' | 'alliance' | 'qualifications';

// --- Sub-Component: Tree Item for Alliance Topology ---
const TreeItem: React.FC<{ node: Institution; allNodes: Institution[]; onSelect: (inst: Institution) => void }> = ({ node, allNodes, onSelect }) => {
  const children = allNodes.filter(n => n.parentId === node.id);
  const [expanded, setExpanded] = useState(true);

  return (
    <div className="ml-8 border-l-2 border-dashed border-gray-300 pl-6 relative py-2">
      <div className="absolute top-5 left-0 w-6 h-0.5 bg-gray-300 border-t border-dashed"></div>
      <div 
        className="flex items-center gap-3 p-3 bg-white border border-gray-200 rounded-lg shadow-sm cursor-pointer hover:border-blue-500 hover:shadow-md transition-all group w-fit min-w-[200px]"
        onClick={(e) => { e.stopPropagation(); onSelect(node); }}
      >
        <span className={`w-3 h-3 rounded-full ${node.type === 'Hospital' ? 'bg-blue-500' : 'bg-green-500'}`}></span>
        <div>
           <div className="text-sm font-bold text-gray-800 group-hover:text-blue-600">{node.name}</div>
           <div className="text-xs text-gray-500 mt-0.5 flex gap-2">
             <span>{node.level}</span>
             {node.parentId ? <span className="bg-blue-50 text-blue-600 px-1 rounded">成员单位</span> : <span className="bg-purple-50 text-purple-600 px-1 rounded">牵头医院</span>}
           </div>
        </div>
      </div>
      {expanded && children.length > 0 && (
        <div className="mt-1">
          {children.map(child => (
            <TreeItem key={child.id} node={child} allNodes={allNodes} onSelect={onSelect} />
          ))}
        </div>
      )}
    </div>
  );
};

