        </div>
        <div className="flex gap-3">
            <Button variant="secondary">
               <FileText className="w-4 h-4 mr-2" /> 生成月度报表
            </Button>
            <Button variant="primary">
               <Download className="w-4 h-4 mr-2" /> 导出Excel
            </Button>
        </div>
      </div>

      {/* City Summary Table */}
      {!selectedCity ? (
        <div className="bg-white rounded-lg shadow-sm border border-gray-200">
          <div className="px-6 py-4 border-b border-gray-200 bg-gray-50">
             <h3 className="font-bold text-gray-700">各市财务汇总统计</h3>
          </div>
          <Table<CityFinancialSummary>
            rowKey="cityCode"
            dataSource={citySummary}
            columns={[
              { title: '城市', render: (r) => <span className="font-bold text-gray-800">{r.cityName}</span> },
              { title: '总交易额', render: (r) => <span className="text-blue-600 font-medium">¥{r.totalAmount.toLocaleString()}</span> },
              { title: '总单量', render: (r) => r.transactionCount },
              { title: '异常/审计预警', render: (r) => r.abnormalCount > 0 ? <Badge type="danger">{r.abnormalCount} 笔异常</Badge> : <Badge type="success">正常</Badge> },
              { title: '操作', render: (r) => (
                 <Button size="sm" variant="ghost" onClick={() => setSelectedCity(r.cityName)}>
                    查看明细 <ChevronRight className="w-4 h-4 ml-1" />
                 </Button>
              )}
            ]}
          />
        </div>
      ) : (
        <div className="bg-white rounded-lg shadow-sm border border-gray-200">
          <div className="px-6 py-4 border-b border-gray-200 flex justify-between items-center">
             <div className="flex items-center gap-2">
                <Button size="sm" variant="ghost" onClick={() => setSelectedCity(null)}>← 返回汇总</Button>
                <h3 className="font-bold text-gray-800 border-l pl-3 ml-2">{selectedCity} - 流水明细查询</h3>
             </div>
             <Button size="sm" variant="secondary">导出当前列表</Button>
          </div>
          <Table<any>
              rowKey="id"
              dataSource={detailDataSource}
              columns={[
                { title: '流水号', render: (r: any) => <span className="font-mono text-xs">{r.id}</span> },
                { title: '项目', render: (r: any) => r.serviceName },
                { title: '机构', render: (r: any) => r.institutionName },
                { title: '金额', render: (r: any) => <span className={isAbnormal(r) ? "text-red-600 font-bold" : ""}>¥{r.amount}</span> },
                { title: '合规性', render: (r: any) => isAbnormal(r) ? <Badge type="danger">偏离 {r.deviation}%</Badge> : <Badge type="success">合规</Badge> },
                { title: '日期', render: (r: any) => r.date },
              ]}
          />
        </div>
      )}
    </div>
