interface Props {
  currentRegionCode: string;
}

export const FinancialScan: React.FC<Props> = () => {
  const [selectedCity, setSelectedCity] = useState<string | null>(null);

  // 1. Mock Aggregation for Cities
  const citySummary: CityFinancialSummary[] = [
    { cityCode: '330100', cityName: '杭州市', totalAmount: 452000.00, transactionCount: 1250, abnormalCount: 12 },
    { cityCode: '330200', cityName: '宁波市', totalAmount: 385000.00, transactionCount: 980, abnormalCount: 5 },
    { cityCode: '330300', cityName: '温州市', totalAmount: 310000.00, transactionCount: 850, abnormalCount: 8 },
  ];

  // 2. Detail View Data (Mock filtering)
  const detailDataSource = MOCK_TRANSACTIONS.map(t => ({
    ...t,
    deviation: ((t.amount - t.standardPrice) / t.standardPrice * 100).toFixed(1)
