type TabType = 'info' | 'alliance' | 'qualifications';

// --- Sub-Component: Tree Item for Alliance Topology ---
const TreeItem: React.FC<{ node: Institution; allNodes: Institution[]; onSelect: (inst: Institution) => void }> = ({ node, allNodes, onSelect }) => {
  const children = allNodes.filter(n => n.parentId === node.id);
  const [expanded, setExpanded] = useState(true);

  return (
    <div className="ml-8 border-l-2 border-dashed border-gray-300 pl-6 relative py-2">
      <div className="absolute top-5 left-0 w-6 h-0.5 bg-gray-300 border-t border-dashed"></div>
      <div 
        className="flex items-center gap-3 p-3 bg-white border border-gray-200 rounded-lg shadow-sm cursor-pointer hover:border-blue-500 hover:shadow-md transition-all group w-fit min-w-[200px]"
        onClick={(e) => { e.stopPropagation(); onSelect(node); }}
      >
        <span className={`w-3 h-3 rounded-full ${node.type === 'Hospital' ? 'bg-blue-500' : 'bg-green-500'}`}></span>
        <div>
           <div className="text-sm font-bold text-gray-800 group-hover:text-blue-600">{node.name}</div>
           <div className="text-xs text-gray-500 mt-0.5 flex gap-2">
             <span>{node.level}</span>
             {node.parentId ? <span className="bg-blue-50 text-blue-600 px-1 rounded">成员单位</span> : <span className="bg-purple-50 text-purple-600 px-1 rounded">牵头医院</span>}
           </div>
        </div>
      </div>
      {expanded && children.length > 0 && (
        <div className="mt-1">
          {children.map(child => (
            <TreeItem key={child.id} node={child} allNodes={allNodes} onSelect={onSelect} />
          ))}
        </div>
      )}
    </div>
  );
};

export const InstitutionManager: React.FC<Props> = ({ currentRegionCode }) => {
  const [activeTab, setActiveTab] = useState<TabType>('info');
  const [selectedInstitution, setSelectedInstitution] = useState<Institution | null>(null);
  const [isDetailOpen, setIsDetailOpen] = useState(false);

  // Mock Data Filtering
  const filteredData = useMemo(() => {
    return MOCK_INSTITUTIONS.filter(inst => inst.regionCode.startsWith(currentRegionCode));
  }, [currentRegionCode]);

  // Mock Qualification Data
  const qualWarnings = [
    { id: 1, name: '南明区花果园社区卫生服务中心', cert: '医疗机构执业许可证', expireDate: '2023-11-01', status: 'Urgent' },
    { id: 2, name: '花果园第二卫生室', cert: '大型设备配置许可(CT)', expireDate: '2023-12-15', status: 'Warning' },
  ];

  const handleSupervise = (item: any) => {
    alert(`已向 [${item.name}] 下发督办通知：\n您的 [${item.cert}] 即将于 ${item.expireDate} 到期，请尽快提交延续申请。`);
  };

  // 1. Medical Institution Information (List: Name, Region, Level, Qualifications)
  const renderInfo = () => (
    <div className="space-y-4">
      <div className="flex justify-between items-center mb-4">
        <h3 className="text-lg font-medium text-gray-800">医疗机构信息列表</h3>
        <Button onClick={() => { setSelectedInstitution(null); setIsDetailOpen(true); }}>
          <Plus className="w-4 h-4 mr-2" /> 新增机构
        </Button>
      </div>
      <Table<Institution>
        rowKey="id"
        dataSource={filteredData}
        columns={[
          { title: '医疗机构名称', render: (r) => <span className="font-medium text-blue-600 cursor-pointer" onClick={() => { setSelectedInstitution(r); setIsDetailOpen(true); }}>{r.name}</span> },
          { title: '所属地', render: (r) => <span className="text-xs text-gray-500 font-mono">{r.regionCode}</span> }, 
          { title: '等级', render: (r) => <Badge type={r.level.includes('三级') ? 'success' : 'neutral'}>{r.level}</Badge> },
          { title: '资质', render: (r) => (
             <div className="flex gap-1 flex-wrap">
               {r.qualifications?.map((q, i) => <span key={i} className="text-[10px] bg-gray-100 border border-gray-200 px-1 rounded text-gray-600">{q}</span>)}
             </div>
          )},
          { title: '状态', render: (r) => <Badge type={r.status === 'Active' ? 'success' : 'danger'}>{r.status === 'Active' ? '正常' : '注销'}</Badge> },
          { title: '操作', render: (r) => (
            <Button size="sm" variant="secondary" onClick={() => { setSelectedInstitution(r); setIsDetailOpen(true); }}>
              <Edit className="w-3 h-3 mr-1" /> 查看
            </Button>
          )}
        ]}
      />
    </div>
  );

  // 2. Alliance Info
  const rootNodes = useMemo(() => {
      const ids = new Set(filteredData.map(d => d.id));
      return filteredData.filter(d => !d.parentId || !ids.has(d.parentId));
  }, [filteredData]);

  const renderAlliance = () => (
    <div className="h-[600px] flex flex-col">
       <div className="flex justify-between items-center mb-4 p-4 bg-blue-50 border border-blue-100 rounded-lg">
          <div>
            <h3 className="font-bold text-blue-900">医联体信息概览</h3>
            <p className="text-sm text-blue-600 mt-1">查看医联体牵头与成员关系网、协议有效期等。</p>
          </div>
          <Button size="sm" variant="primary">
            <GitBranch className="w-4 h-4 mr-2" /> 协议管理
          </Button>
       </div>
       <div className="flex-1 bg-gray-50 p-8 rounded-xl border border-gray-200 shadow-inner overflow-auto">
         {rootNodes.length > 0 ? rootNodes.map(root => (
            <TreeItem 
              key={root.id} 
              node={root} 
              allNodes={filteredData} 
              onSelect={(inst) => { setSelectedInstitution(inst); setIsDetailOpen(true); }}
            />
         )) : <div className="text-center text-gray-400 mt-10">当前区域暂无医联体数据</div>}
       </div>
    </div>
  );

  // 3. Institution Qualification Management
  const renderQualifications = () => (
    <div className="space-y-6">
      <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
         <div className="bg-white p-4 rounded-lg border border-gray-200 shadow-sm">
            <div className="text-gray-500 text-sm">执业许可持证率</div>
            <div className="text-2xl font-bold text-gray-800">100%</div>
         </div>
         <div className="bg-red-50 p-4 rounded-lg border border-red-100">
            <div className="text-red-600 text-sm">资质临期预警</div>
            <div className="text-2xl font-bold text-red-800">8 <span className="text-sm font-normal">家</span></div>
         </div>
         <div className="bg-blue-50 p-4 rounded-lg border border-blue-100">
            <div className="text-blue-600 text-sm">大型设备配置</div>
            <div className="text-2xl font-bold text-blue-800">42 <span className="text-sm font-normal">台</span></div>
         </div>
      </div>

      <div className="bg-white border border-gray-200 rounded-lg">
        <div className="px-6 py-4 border-b border-gray-200 flex justify-between items-center">
           <h3 className="font-medium text-gray-800">资质管理列表</h3>
        </div>
        <Table
          rowKey="id"
          dataSource={qualWarnings}
          columns={[
            { title: '机构名称', render: (r:any) => r.name },
            { title: '资质名称', render: (r:any) => <span className="font-medium">{r.cert}</span> },
            { title: '到期日期', render: (r:any) => r.expireDate },
            { title: '状态', render: (r:any) => <Badge type="warning">即将到期</Badge> },
            { title: '操作', render: (r:any) => (
               <Button size="sm" variant="secondary" onClick={() => handleSupervise(r)}>
                 <Send className="w-3 h-3 mr-2" /> 督办
               </Button>
            )}
          ]}
        />
      </div>
    </div>
  );

  return (
    <div className="flex flex-col h-full space-y-4">
      {/* Tabs Header */}
      <div className="flex space-x-1 bg-gray-100 p-1 rounded-lg w-fit border border-gray-200">
        {[
          { id: 'info', label: '医疗机构信息', icon: FileClock },
          { id: 'alliance', label: '医联体信息', icon: Network },
          { id: 'qualifications', label: '机构资质管理', icon: ShieldCheck },
        ].map(tab => (
          <button
            key={tab.id}
            onClick={() => setActiveTab(tab.id as TabType)}
            className={`
              flex items-center px-4 py-2 text-sm font-medium rounded-md transition-all
              ${activeTab === tab.id 
                ? 'bg-white text-blue-600 shadow-sm' 
                : 'text-gray-500 hover:text-gray-700 hover:bg-gray-200'}
            `}
          >
            <tab.icon className="w-4 h-4 mr-2" />
            {tab.label}
          </button>
        ))}
      </div>

      {/* Main Content Area */}
      <div className="flex-1 bg-white rounded-lg border border-gray-200 shadow-sm p-6 overflow-auto">
        {activeTab === 'info' && renderInfo()}
        {activeTab === 'alliance' && renderAlliance()}
        {activeTab === 'qualifications' && renderQualifications()}
      </div>

      {/* Detail Modal */}
      <Modal 
        isOpen={isDetailOpen} 
        onClose={() => setIsDetailOpen(false)} 
        title={selectedInstitution ? `机构详情: ${selectedInstitution.name}` : "新增机构"}
      >
        <div className="space-y-4">
           {/* Simple Detail View */}
           <div className="p-4 bg-gray-50 rounded">
              <p><strong>名称：</strong> {selectedInstitution?.name || '新机构'}</p>
              <p><strong>地址：</strong> {selectedInstitution?.address}</p>
              <p><strong>等级：</strong> {selectedInstitution?.level}</p>
           </div>
        </div>
      </Modal>
    </div>
  );
};

// Administrative Hierarchy Levels
export enum AdminLevel {
  PROVINCE = 1, // Global View
  CITY = 2,     // City View
  COUNTY = 3,   // County View
  VILLAGE = 4   // Execution Unit
}

export interface Region {
  code: string;
  name: string;
  level: AdminLevel;
  parentCode: string | null;
}

export interface User {
  id: string;
  name: string;
  role: string;
  adminLevel: AdminLevel;
  regionCode: string; // The region this user belongs to
  avatar?: string;
}

export interface RouteConfig {
  key: string;
  path: string;
  label: string;
  icon: string;
  // Permissions
  minLevel?: AdminLevel; 
  maxLevel?: AdminLevel;
  includeLevels?: AdminLevel[]; // Explicitly allowed levels (overrides min/max if present)

  features?: string[];
  children?: RouteConfig[]; // Nested menu items
}

// Institution / Grid Management
export interface Institution {
  id: string;
  name: string;
  type: 'Hospital' | 'HealthCenter' | 'VillageClinic';
  level: string; // Grade 3A, etc.
  regionCode: string;
  address: string;
  parentId?: string; // For Medical Alliance tree
  status: 'Active' | 'Inactive';
  qualifications?: string[]; // e.g. ["Medical License", "Radiology Permit"]
}

// Resource Supervision
export interface Doctor {
  id: string;
  name: string;
  title: string; // Chief Physician, etc.
  institutionName: string;
  specialty: string;
  serviceCount: number;
  rating: number;
}

// Operation Stats
export interface OperationStat {
  regionName: string;
  regionCode: string; // Added for linking
  parentCode: string | null; // Added for hierarchical filtering
  telemedicineCount: number;
  internetMedCount: number;
  totalWorkload: number;
}

// Warning Center
export interface WarningItem {
  id: string;
  title: string;
  type: 'CertExpired' | 'DeviceOffline' | 'StaffShortage';
  severity: 'High' | 'Medium' | 'Low';
  institutionName: string;
  date: string;
  status: 'Pending' | 'Processing' | 'Resolved';
}

// Financial
export interface Transaction {
  id: string;
  serviceName: string;
  institutionName: string;
  regionName: string; // Added for city aggregation
  amount: number;
  standardPrice: number;
  date: string;
  status: 'Normal' | 'Abnormal';
  patientName: string;
}

export interface CityFinancialSummary {
  cityCode: string;
  cityName: string;
  totalAmount: number;
  transactionCount: number;
  abnormalCount: number;
}

