                <div className="text-gray-500 mb-1">互联网医疗业务总量</div>
                <div className="text-3xl font-bold text-green-600">
                    {filteredOperations.reduce((sum, item) => sum + item.internetMedCount, 0).toLocaleString()}
                </div>
             </div>
             <div>
                <div className="text-gray-500 mb-1">区域总工作量</div>
                <div className="text-3xl font-bold text-orange-600">
                    {filteredOperations.reduce((sum, item) => sum + item.totalWorkload, 0).toLocaleString()}
