import React, { useState, useMemo } from 'react';
import { MOCK_INSTITUTIONS } from '../constants';
import { Institution } from '../types';
import { Button, Table, Badge, Modal } from './UI';
import { 
  FileClock, Network, ShieldCheck, 
  Eye, FileText, ChevronDown, ChevronRight, Paperclip, BarChart3
} from 'lucide-react';

interface Props {
  currentRegionCode: string;
}

type TabType = 'info' | 'alliance' | 'qualifications';

// --- Sub-Component: Enhanced Tree Item for Alliance ---
const TreeItem: React.FC<{ node: Institution; allNodes: Institution[]; isRoot?: boolean }> = ({ node, allNodes, isRoot = false }) => {
  const children = allNodes.filter(n => n.parentId === node.id);
  const [expanded, setExpanded] = useState(true);
  const [showDetails, setShowDetails] = useState(false);

  return (
    <div className={`relative ${!isRoot ? 'ml-8 border-l-2 border-dashed border-gray-300 pl-6 py-2' : 'py-2'}`}>
      {!isRoot && <div className="absolute top-5 left-0 w-6 h-0.5 bg-gray-300 border-t border-dashed"></div>}

      <div className="bg-white border border-gray-200 rounded-lg shadow-sm transition-all hover:shadow-md overflow-hidden w-full max-w-4xl">
        <div 
          className="flex items-center justify-between p-3 cursor-pointer bg-gray-50/50 hover:bg-gray-50"
          onClick={() => setShowDetails(!showDetails)}
        >
          <div className="flex items-center gap-3">
             <button 
               onClick={(e) => { e.stopPropagation(); setExpanded(!expanded); }}
               className={`p-1 rounded hover:bg-gray-200 text-gray-500 ${children.length === 0 ? 'invisible' : ''}`}
             >
               {expanded ? <ChevronDown size={16} /> : <ChevronRight size={16} />}
             </button>
             <span className={`w-3 h-3 rounded-full ${node.type === 'Hospital' ? 'bg-blue-500' : 'bg-green-500'}`}></span>
             <div>
                <div className="text-sm font-bold text-gray-800 flex items-center gap-2">
                  {node.name}
                  {isRoot && <Badge type="neutral">牵头医院</Badge>}
                  {!isRoot && <Badge type="success">成员单位</Badge>}
                </div>
             </div>
          </div>
          <div className="flex gap-4 text-xs text-gray-500 mr-4">
             <span>{node.level}</span>
             <span>{node.type === 'Hospital' ? '综合医院' : '基层卫生机构'}</span>
          </div>
        </div>

        {/* Inline Details Panel */}
        {showDetails && (
          <div className="p-4 bg-blue-50/30 border-t border-gray-100 grid grid-cols-2 gap-4 text-sm">
             <div>
                <span className="text-gray-400 block text-xs mb-1">地址</span>
                <span className="text-gray-700">{node.address}</span>
             </div>
             <div>
                <span className="text-gray-400 block text-xs mb-1">资质许可</span>
                <div className="flex flex-wrap gap-1">
                  {node.qualifications?.map((q, i) => (
                    <span key={i} className="px-2 py-0.5 bg-white border border-gray-200 rounded text-xs text-gray-600">{q}</span>
                  ))}
                </div>
             </div>
             <div>
                <span className="text-gray-400 block text-xs mb-1">运行状态</span>
                <Badge type={node.status === 'Active' ? 'success' : 'danger'}>{node.status}</Badge>
             </div>
          </div>
        )}
      </div>

      {expanded && children.length > 0 && (
        <div className="mt-1">
          {children.map(child => (
            <TreeItem key={child.id} node={child} allNodes={allNodes} />
          ))}
        </div>
      )}
    </div>
  );
};

export const InstitutionManager: React.FC<Props> = ({ currentRegionCode }) => {
  const [activeTab, setActiveTab] = useState<TabType>('info');

  // Filter Data
  const filteredData = useMemo(() => {
    return MOCK_INSTITUTIONS.filter(inst => inst.regionCode.startsWith(currentRegionCode));
  }, [currentRegionCode]);

  // Statistics for Charts
  const statsByLevel = useMemo(() => {
     const stats: Record<string, number> = {};
     filteredData.forEach(d => {
        stats[d.level] = (stats[d.level] || 0) + 1;
     });
     return stats;
  }, [filteredData]);

  // Mock Qualification Data
  const qualList = [
    { id: 1, name: '南明区花果园社区卫生服务中心', cert: '医疗机构执业许可证', expireDate: '2023-11-01', rate: '100%', file: 'cert_001.pdf' },
    { id: 2, name: '花果园第二卫生室', cert: '大型设备配置许可(CT)', expireDate: '2023-12-15', rate: '100%', file: 'cert_002.pdf' },
    { id: 3, name: '贵阳市第一人民医院', cert: '放射诊疗许可证', expireDate: '2024-05-20', rate: '100%', file: 'cert_003.pdf' },
  ];

  // 1. Info Tab: Chart + List (No Edit/Create)
  const renderInfo = () => (
    <div className="space-y-6">
      {/* Visual Chart Area (CSS Bar Chart) */}
      <div className="bg-white p-6 rounded-lg border border-gray-200">
         <h3 className="text-sm font-bold text-gray-700 mb-4 flex items-center gap-2">
            <BarChart3 className="w-4 h-4"/> 医疗机构等级分布统计
         </h3>
         <div className="flex items-end gap-8 h-32 border-b border-gray-200 pb-2 px-4">
            {Object.entries(statsByLevel).map(([level, count], idx) => (
              <div key={level} className="flex flex-col items-center gap-2 flex-1 group">
                 <div className="relative w-full max-w-[60px] flex items-end h-full">
                    <div 
                      className="w-full bg-blue-500 rounded-t opacity-80 group-hover:opacity-100 transition-all"
                      style={{ height: `${(Number(count) / filteredData.length) * 100}%`, minHeight: '4px' }}
                    ></div>
                    <span className="absolute -top-6 w-full text-center text-sm font-bold text-blue-600">{count}</span>
                 </div>
                 <span className="text-xs text-gray-500 font-medium text-center truncate w-full">{level}</span>
              </div>
            ))}
         </div>
      </div>

      {/* List Table */}
      <div className="bg-white border border-gray-200 rounded-lg overflow-hidden">
        <div className="px-6 py-4 border-b border-gray-200 bg-gray-50 flex justify-between items-center">
           <h3 className="font-bold text-gray-800">医疗机构信息明细</h3>
           <span className="text-xs text-gray-500">共 {filteredData.length} 家机构</span>
        </div>
        <Table<Institution>
          rowKey="id"
          dataSource={filteredData}
          columns={[
            { title: '医疗机构名称', render: (r) => <span className="font-medium text-gray-900">{r.name}</span> },
            { title: '所属地', render: (r) => <span className="text-xs text-gray-500 font-mono">{r.regionCode}</span> }, 
            { title: '等级', render: (r) => <Badge type={r.level.includes('三级') ? 'success' : 'neutral'}>{r.level}</Badge> },
            { title: '资质', render: (r) => (
               <div className="flex gap-1 flex-wrap">
                 {r.qualifications?.map((q, i) => <span key={i} className="text-[10px] bg-gray-100 border border-gray-200 px-1 rounded text-gray-600">{q}</span>)}
               </div>
            )},
            { title: '状态', render: (r) => <Badge type={r.status === 'Active' ? 'success' : 'danger'}>{r.status === 'Active' ? '正常' : '注销'}</Badge> },
            { title: '操作', render: (r) => (
              <Button size="sm" variant="ghost">
                <Eye className="w-3 h-3 mr-1" /> 查看详情
              </Button>
            )}
          ]}
        />
      </div>
    </div>
  );

  // 2. Alliance Info: Inline Tree
  const rootNodes = useMemo(() => {
      const ids = new Set(filteredData.map(d => d.id));
      return filteredData.filter(d => !d.parentId || !ids.has(d.parentId));
  }, [filteredData]);

  const renderAlliance = () => (
    <div className="h-full flex flex-col">
       <div className="flex justify-between items-center mb-4 p-4 bg-blue-50 border border-blue-100 rounded-lg">
          <div>
            <h3 className="font-bold text-blue-900">医联体信息概览</h3>
            <p className="text-sm text-blue-600 mt-1">展示牵头医院与成员单位的层级关系及机构详细资质。</p>
          </div>
       </div>
       <div className="flex-1 bg-white p-6 rounded-xl border border-gray-200 shadow-sm overflow-auto">
         {rootNodes.length > 0 ? rootNodes.map(root => (
            <TreeItem 
              key={root.id} 
              node={root} 
              allNodes={filteredData} 
              isRoot={true}
            />
         )) : <div className="text-center text-gray-400 mt-10">当前区域暂无医联体数据</div>}
       </div>
    </div>
  );

  // 3. Qualifications: Attachment View, No Supervise
  const renderQualifications = () => (
    <div className="space-y-6">
      <div className="bg-white border border-gray-200 rounded-lg">
        <div className="px-6 py-4 border-b border-gray-200 flex justify-between items-center">
           <h3 className="font-medium text-gray-800">资质管理列表</h3>
        </div>
        <Table
          rowKey="id"
          dataSource={qualList}
          columns={[
            { title: '机构名称', render: (r:any) => r.name },
            { title: '资质名称', render: (r:any) => <span className="font-medium">{r.cert}</span> },
            { title: '有效期至', render: (r:any) => r.expireDate },
            { title: '持证率', render: (r:any) => <span className="text-green-600 font-bold">{r.rate}</span> },
            { title: '附件', render: (r:any) => (
               <div className="flex items-center text-blue-600 cursor-pointer hover:underline text-xs">
                  <Paperclip className="w-3 h-3 mr-1" />
                  {r.file}
               </div>
            )},
            { title: '状态', render: (r:any) => <Badge type="warning">即将到期</Badge> },
          ]}
        />
      </div>
    </div>
  );

  return (
    <div className="flex flex-col h-full space-y-4">
      {/* Tabs Header */}
      <div className="flex space-x-1 bg-gray-100 p-1 rounded-lg w-fit border border-gray-200">
        {[
          { id: 'info', label: '医疗机构信息列表', icon: FileClock },
          { id: 'alliance', label: '医联体展示', icon: Network },
          { id: 'qualifications', label: '资质管理', icon: ShieldCheck },
        ].map(tab => (
          <button
            key={tab.id}
            onClick={() => setActiveTab(tab.id as TabType)}
            className={`
              flex items-center px-4 py-2 text-sm font-medium rounded-md transition-all
              ${activeTab === tab.id 
                ? 'bg-white text-blue-600 shadow-sm' 
                : 'text-gray-500 hover:text-gray-700 hover:bg-gray-200'}
            `}
          >
            <tab.icon className="w-4 h-4 mr-2" />
            {tab.label}
          </button>
        ))}
      </div>

      {/* Main Content Area */}
      <div className="flex-1 bg-gray-50/50 rounded-lg p-1 overflow-auto">
        {activeTab === 'info' && renderInfo()}
        {activeTab === 'alliance' && renderAlliance()}
        {activeTab === 'qualifications' && renderQualifications()}
      </div>
    </div>
  );
};

