              <Trophy className={`w-3 h-3 text-${color}-600`} /> {title}
          </h3>
          <TrendingUp className="w-3 h-3 text-gray-400" />
       </div>
       <div className="p-3 flex-1 flex flex-col justify-center">
          <div className="space-y-3">
              {data.map((item, idx) => {
                  const val = parseVal(item.value);
                  const percentage = Math.round((val / maxVal) * 100);

                  return (
                    <div key={idx} className="flex flex-col gap-1">
                        <div className="flex justify-between items-end text-xs leading-none">
                            <div className="flex items-center gap-2 overflow-hidden">
                                <span className={`flex items-center justify-center w-3.5 h-3.5 rounded text-[9px] font-bold shrink-0 ${idx < 3 ? `bg-${color}-100 text-${color}-700` : 'bg-gray-100 text-gray-400'}`}>
                                    {idx + 1}
                                </span>
                                <span className="text-gray-700 truncate font-medium text-[11px]" title={item.name}>{item.name}</span>
                            </div>
                            <span className="font-mono font-bold text-gray-800 text-[10px]">{item.value}</span>
                        </div>
                        {/* Progress Bar */}
                        <div className="h-1.5 w-full bg-gray-100 rounded-full overflow-hidden">
                            <div 
                                className={`h-full rounded-full ${getColorClass(color)} opacity-80`} 
                                style={{ width: `${percentage}%` }} 
                            />
                        </div>
                    </div>
                  );
              })}
          </div>
       </div>
    </div>
  );
};

