          <TrendingUp className="w-3 h-3 text-gray-400" />
       </div>
       <div className="p-4 flex-1 flex flex-col min-h-0">
          {/* Changed to flex-col with justify-between to distribute items evenly across the taller height */}
          <div className="flex flex-col justify-between h-full py-1">
              {data.map((item, idx) => {
                  const val = parseVal(item.value);
                  const percentage = Math.round((val / maxVal) * 100);

                  return (
                    <div key={idx} className="flex flex-col gap-1.5">
                        <div className="flex justify-between items-end text-xs leading-none">
                            <div className="flex items-center gap-2 overflow-hidden">
                                <span className={`flex items-center justify-center w-4 h-4 rounded text-[10px] font-bold shrink-0 ${idx < 3 ? `bg-${color}-100 text-${color}-700` : 'bg-gray-100 text-gray-400'}`}>
                                    {idx + 1}
                                </span>
                                <span className="text-gray-700 truncate font-medium text-[12px]" title={item.name}>{item.name}</span>
                            </div>
                            <span className="font-mono font-bold text-gray-800 text-[12px]">{item.value}</span>
                        </div>
                        {/* Slightly thicker bar for better visibility in taller card */}
                        <div className="h-2.5 w-full bg-gray-100 rounded-full overflow-hidden">
                            <div 
                                className={`h-full rounded-full ${getColorClass(color)} transition-all duration-500`} 
                                style={{ width: `${percentage}%` }} 
                            />
                        </div>
                    </div>
                  );
              })}
          </div>
       </div>
    </div>
  );
};

