// --- Helper: Matrix Stats Component ---
const StatsMatrix = ({ data }: { data: Institution[] }) => {
  // Initialize matrix counters: [Level 3, Level 2, Level 1/Unrated] x [A, B, C/Unrated]
  const matrix = {
    l3: { a: 0, b: 0, c: 0 },
    l2: { a: 0, b: 0, c: 0 },
    l1: { a: 0, b: 0, c: 0 },
  };

  data.forEach(inst => {
    let row: 'l3' | 'l2' | 'l1' = 'l1';
    let col: 'a' | 'b' | 'c' = 'c';

    if (inst.level.includes('三级')) row = 'l3';
    else if (inst.level.includes('二级')) row = 'l2';

    if (inst.level.includes('甲等')) col = 'a';
    else if (inst.level.includes('乙等')) col = 'b';

    matrix[row][col]++;
  });

  const Cell = ({ val, color }: { val: number, color: string }) => (
    <div className={`text-center py-3 rounded-md ${val > 0 ? `bg-${color}-50 text-${color}-700 font-bold` : 'bg-gray-50 text-gray-300'}`}>
      {val}
    </div>
  );

  return (
    <div className="bg-white p-5 rounded-lg border border-gray-200">
      <h3 className="text-sm font-bold text-gray-700 mb-4 flex items-center gap-2">
        <Grid className="w-4 h-4"/> 医疗机构等级分布矩阵
      </h3>
      <div className="grid grid-cols-4 gap-2 text-sm">
        {/* Header Row */}
        <div className="text-gray-400 font-medium flex items-center justify-center">等级 \ 类别</div>
        <div className="text-center font-bold text-gray-600 bg-gray-100 py-2 rounded">甲等</div>
        <div className="text-center font-bold text-gray-600 bg-gray-100 py-2 rounded">乙等</div>
        <div className="text-center font-bold text-gray-600 bg-gray-100 py-2 rounded">丙等/未定</div>

        {/* Level 3 Row */}
        <div className="font-bold text-blue-800 bg-blue-100 py-3 rounded flex items-center justify-center">三级医院</div>
        <Cell val={matrix.l3.a} color="blue" />
        <Cell val={matrix.l3.b} color="blue" />
        <Cell val={matrix.l3.c} color="blue" />

        {/* Level 2 Row */}
        <div className="font-bold text-green-800 bg-green-100 py-3 rounded flex items-center justify-center">二级医院</div>
        <Cell val={matrix.l2.a} color="green" />
        <Cell val={matrix.l2.b} color="green" />
        <Cell val={matrix.l2.c} color="green" />

        {/* Level 1 Row */}
        <div className="font-bold text-orange-800 bg-orange-100 py-3 rounded flex items-center justify-center">一级/其他</div>
        <Cell val={matrix.l1.a} color="orange" />
        <Cell val={matrix.l1.b} color="orange" />
        <Cell val={matrix.l1.c} color="orange" />
      </div>
    </div>
  );
};

