// --- 3. Enhanced Tree Item for Alliance (1 Lead + 4 Affiliates Visuals) ---
const TreeItem: React.FC<{ node: Institution; allNodes: Institution[]; isRoot?: boolean }> = ({ node, allNodes, isRoot = false }) => {
  const children = allNodes.filter(n => n.parentId === node.id);

  // Always expand root to show the 1+4 structure immediately
  const [expanded, setExpanded] = useState(true); 
  const [showDetails, setShowDetails] = useState(false);

  return (
    <div className="relative">
      {/* Connector Lines Logic */}
      {!isRoot && (
         <>
           {/* Vertical line from parent */}
           <div className="absolute -top-4 -left-5 w-px h-10 bg-gray-300 border-l border-dashed border-gray-300"></div>
           {/* Horizontal line to node */}
           <div className="absolute top-6 -left-5 w-5 h-px bg-gray-300 border-t border-dashed border-gray-300"></div>
         </>
      )}

      <div className={`relative ${!isRoot ? 'ml-8 mb-4' : 'mb-6'}`}>
        <div className={`bg-white border rounded-lg shadow-sm transition-all hover:shadow-md overflow-hidden w-full max-w-5xl
            ${isRoot ? 'border-blue-200 ring-2 ring-blue-50' : 'border-gray-200'}
        `}>
          <div className="flex items-center justify-between p-3 bg-white">
            <div className="flex items-center gap-3 flex-1">
               {/* Expander Icon */}
               <button 
                 onClick={() => setExpanded(!expanded)}
                 className={`p-1 rounded hover:bg-gray-100 text-gray-500 transition-transform ${children.length === 0 ? 'invisible' : ''}`}
               >
                 {expanded ? <ChevronDown size={16} /> : <ChevronRight size={16} />}
               </button>

               {/* Icon & Name */}
               <div className={`w-8 h-8 rounded-lg flex items-center justify-center text-white shrink-0 shadow-sm
                  ${node.type === 'Hospital' ? (isRoot ? 'bg-blue-600' : 'bg-indigo-500') : 'bg-green-500'}
               `}>
                  <Building size={16} />
               </div>

               <div className="flex flex-col">
                  <div className="flex items-center gap-2">
                    <span className="text-sm font-bold text-gray-800">{node.name}</span>
                    {isRoot && <Badge type="neutral">牵头单位</Badge>}
                    {!isRoot && <Badge type="success">附属单位</Badge>}
                  </div>
                  <span className="text-[10px] text-gray-400">{node.address}</span>
               </div>
            </div>

            {/* Right Side Actions */}
            <div className="flex items-center gap-4">
               <div className="text-right hidden sm:block">
                  <div className="text-xs font-medium text-gray-600">{node.level}</div>
                  <div className="text-[10px] text-gray-400">科室: {node.departmentCount || 0}</div>
               </div>
               <div className="h-8 w-px bg-gray-100 mx-2"></div>
               <Button 
                  size="sm" 
                  variant={showDetails ? 'primary' : 'secondary'} 
                  onClick={() => setShowDetails(!showDetails)}
                  className="flex items-center gap-1"
                >
                  <Info size={14} />
                  详情
               </Button>
            </div>
          </div>

          {/* Inline Details Panel (Expandable) */}
          {showDetails && (
            <div className="border-t border-gray-100 bg-gray-50/50 p-4 animate-in slide-in-from-top-2 duration-200">
               <div className="grid grid-cols-4 gap-4 text-sm">
                  <div className="space-y-1">
                     <span className="text-xs text-gray-400">机构类型</span>
                     <div className="font-medium text-gray-700">{node.type === 'Hospital' ? '综合公立医院' : '基层卫生机构'}</div>
                  </div>
                   <div className="space-y-1">
                     <span className="text-xs text-gray-400">法人代表</span>
                     <div className="font-medium text-gray-700">{node.legalRepresentative || '未录入'}</div>
                  </div>
                  <div className="space-y-1">
                     <span className="text-xs text-gray-400">联系电话</span>
                     <div className="font-medium text-gray-700">{node.contactPhone || '未录入'}</div>
                  </div>
                  <div className="space-y-1">
                     <span className="text-xs text-gray-400">资质许可</span>
                     <div className="flex flex-wrap gap-1">
                        {node.qualifications?.slice(0, 2).map((q, i) => (
                           <span key={i} className="text-[10px] px-1.5 py-0.5 bg-white border rounded text-gray-600">{q}</span>
                        ))}
                        {(node.qualifications?.length || 0) > 2 && <span className="text-[10px] text-gray-400">...</span>}
                     </div>
                  </div>
               </div>
            </div>
          )}
        </div>

        {/* Children Rendering */}
        {expanded && children.length > 0 && (
          <div className="mt-4 border-l border-dashed border-gray-300 ml-[1.65rem] pl-6 pb-2">
            {children.map(child => (
              <TreeItem key={child.id} node={child} allNodes={allNodes} />
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

