// --- 6. Operation Supervision ---
interface OperationProps {
  activeRegion: Region;
}

export const OperationSupervision: React.FC<OperationProps> = ({ activeRegion }) => {

  // Filter Logic: Show only children of the currently active region
  const filteredOperations = useMemo(() => {
     return MOCK_OPERATIONS.filter(op => op.parentCode === activeRegion.code);
  }, [activeRegion]);

  // Dynamic Title logic
  const getTableTitle = () => {
     if (activeRegion.level === 1) return '各市运行情况统计'; // Province View
     if (activeRegion.level === 2) return '各区/县运行情况统计'; // City View
     if (activeRegion.level === 3) return '各街道/乡镇运行情况统计'; // County View
     return '运行情况统计';
  };

  const getRegionColumnTitle = () => {
     if (activeRegion.level === 1) return '市级区域';
     if (activeRegion.level === 2) return '区县区域';
     if (activeRegion.level === 3) return '乡镇/街道';
     return '区域';
  };

  return (
    <div className="space-y-6">
       {/* Summary Stats */}
       <div className="bg-white p-6 rounded-lg border border-gray-200 shadow-sm">
          <div className="flex justify-between items-start mb-4">
            <h3 className="font-bold text-gray-800">
               {activeRegion.name} - 工作量汇总
            </h3>
            <span className="text-xs bg-gray-100 text-gray-500 px-2 py-1 rounded">实时统计</span>
          </div>

          <div className="grid grid-cols-3 gap-8 text-center divide-x">
             <div>
                <div className="text-gray-500 mb-1">远程医疗业务总量</div>
                <div className="text-3xl font-bold text-blue-600">
                   {/* Sum up filtered results for demo purposes, or use static big number if mock data is limited */}
                   {filteredOperations.reduce((sum, item) => sum + item.telemedicineCount, 0).toLocaleString()}
                </div>
             </div>
             <div>
                <div className="text-gray-500 mb-1">互联网医疗业务总量</div>
                <div className="text-3xl font-bold text-green-600">
                    {filteredOperations.reduce((sum, item) => sum + item.internetMedCount, 0).toLocaleString()}
                </div>
             </div>
             <div>
                <div className="text-gray-500 mb-1">区域总工作量</div>
                <div className="text-3xl font-bold text-orange-600">
                    {filteredOperations.reduce((sum, item) => sum + item.totalWorkload, 0).toLocaleString()}
                </div>
             </div>
          </div>
       </div>

       {/* Region Stats Table */}
       <div className="bg-white border border-gray-200 rounded-lg overflow-hidden">
          <div className="px-6 py-4 border-b border-gray-200 bg-gray-50">
             <h3 className="font-bold text-gray-800">{getTableTitle()}</h3>
          </div>
          <Table 
             rowKey="regionName"
             dataSource={filteredOperations}
             columns={[
                { title: getRegionColumnTitle(), render: (r:any) => <span className="font-medium">{r.regionName}</span> },
                { title: '远程医疗工作量', render: (r:any) => r.telemedicineCount.toLocaleString() },
                { title: '互联网医疗工作量', render: (r:any) => r.internetMedCount.toLocaleString() },
                { title: '总工作量', render: (r:any) => <span className="font-bold">{r.totalWorkload.toLocaleString()}</span> },
                { title: '趋势', render: () => <span className="text-green-500">↗ 5%</span> },
                { title: '操作', render: () => <OperationColumn /> }
             ]}
          />
       </div>
    </div>
  );
};

