// --- 6. Operation Supervision ---
export const OperationSupervision = ({ activeRegion }: { activeRegion: Region }) => {
  // Filter operation stats based on active region or its children
  const stats = useMemo(() => {
     // If province, show city data. If city, show county data.
     let list = MOCK_OPERATIONS.filter(op => op.parentCode === activeRegion.code);
     if (list.length === 0) {
        // Fallback: if no children found (lowest level), show itself
        list = MOCK_OPERATIONS.filter(op => op.regionCode === activeRegion.code);
     }
     return list;
  }, [activeRegion]);

  return (
    <div className="space-y-6 animate-in fade-in duration-300">
       <div className="bg-white p-6 rounded-lg border border-gray-200 shadow-sm mb-4">
          <div className="flex justify-between items-center">
             <div>
                <h2 className="text-xl font-bold text-gray-800">区域运行情况监管</h2>
                <p className="text-gray-500 text-sm mt-1">当前区域: {activeRegion.name} ({activeRegion.code})</p>
             </div>
             <Button><Download className="w-4 h-4 mr-2"/> 导出运行报告</Button>
          </div>
       </div>

       <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
          <StatCard label="区域总业务量" value={stats.reduce((acc, cur) => acc + cur.totalWorkload, 0)} subtext="累计人次" icon={Activity} color="blue" />
          <StatCard label="远程会诊占比" value="35%" subtext="核心业务" icon={Users} color="indigo" />
          <StatCard label="活跃机构数" value={stats.length * 5 + 12} subtext="本周有业务发生" icon={Building2} color="green" />
       </div>

       <div className="bg-white border border-gray-200 rounded-lg overflow-hidden">
        <div className="px-6 py-4 border-b border-gray-200 bg-gray-50">
            <h3 className="font-bold text-gray-800">下辖区域运行数据对比</h3>
        </div>
        <Table<OperationStat>
           rowKey="regionCode"
           dataSource={stats}
           columns={[
             { title: '区域名称', render: (r) => <span className="font-bold">{r.regionName}</span> },
             { title: '远程会诊量', render: (r) => r.telemedicineCount },
             { title: '互联网诊疗量', render: (r) => r.internetMedCount },
             { title: '总业务负荷', render: (r) => <span className="text-blue-600 font-bold">{r.totalWorkload}</span> },
             { title: '同比', render: () => <span className="text-red-500 text-xs flex items-center"><TrendingUp className="w-3 h-3 mr-1"/> +5.2%</span> },
             { title: '操作', render: () => <Button size="sm" variant="ghost"><BarChart2 className="w-3 h-3 mr-1"/> 分析</Button> }
           ]}
        />
       </div>
    </div>
  );
};

