            { title: '操作', render: (r) => (
              <Button size="sm" variant="ghost" onClick={() => setSelectedInst(r)}>
                <Eye className="w-3 h-3 mr-1" /> 详情
              </Button>
            )}
          ]}
        />
      </div>
    </div>
  );

