     // Generate aggregated stats based on filtered institutions
     const generateStats = (services: string[], type: string) => services.map((s, i) => {
        // Mock calculations: percentage of filtered institutions that have this service
        // Deterministic mock using char codes
        const seed = s.charCodeAt(0) + filteredInsts.length; 
        const instCount = Math.ceil(filteredInsts.length * ((seed % 80 + 20) / 100)); 
        const docCount = instCount * (seed % 5 + 2);

        return {
           id: `${type}-${i}`,
           name: s,
           type: type,
           instCount: instCount,
           docCount: docCount,
           totalVolume: instCount * (seed % 50 + 100)
