import React, { useState, useEffect, useMemo } from 'react';
import { 
  LayoutDashboard, Building2, AlertTriangle, FileCheck, ScanLine, 
  ChevronDown, ChevronRight, Bell, LogOut, Map, Search, User as UserIcon,
  Database, ScrollText, Activity, BadgeCheck, Scale, Settings, CheckSquare,
  Network, Wifi, Stethoscope, FileText, List, User
} from 'lucide-react';
import { AdminLevel, Region, User as UserType } from './types';
import { MOCK_USERS, MOCK_REGIONS, ROUTE_CONFIG } from './constants';
import { InstitutionManager } from './components/InstitutionManager';
import { WarningCenter } from './components/WarningCenter';
import { FinancialScan } from './components/FinancialScan';
import { ServiceSupervision, OperationSupervision, QualitySupervision } from './components/SupervisionViews';
import { ResourceSupervision } from './components/ResourceSupervision';
import { ProvincialDashboard, CityDashboard, CountyDashboard } from './components/Dashboards';
import { Button } from './components/UI';

export default function App() {
  const [currentUser, setCurrentUser] = useState<UserType>(MOCK_USERS.provinceAdmin);
  const [currentRoute, setCurrentRoute] = useState<string>('/dashboard');
  const [selectedRegionCode, setSelectedRegionCode] = useState<string>(currentUser.regionCode);
  const [expandedMenus, setExpandedMenus] = useState<Record<string, boolean>>({'system': true});

  useEffect(() => {
    setSelectedRegionCode(currentUser.regionCode);
    setCurrentRoute('/dashboard');
  }, [currentUser]);

  const activeRegion = useMemo(() => 
    MOCK_REGIONS.find(r => r.code === selectedRegionCode) || MOCK_REGIONS[0], 
    [selectedRegionCode]
  );

  const subRegions = useMemo(() => 
    MOCK_REGIONS.filter(r => r.parentCode === activeRegion.code),
    [activeRegion]
  );

  const menuItems = useMemo(() => 
    ROUTE_CONFIG.filter(route => {
      // 1. Explicit inclusion (New Logic)
      if (route.includeLevels) {
        return route.includeLevels.includes(currentUser.adminLevel);
      }
      // 2. Fallback to Min/Max logic
      if (route.minLevel !== undefined) {
         if (currentUser.adminLevel < route.minLevel) return false;
      }
      if (route.maxLevel !== undefined) {
         if (currentUser.adminLevel > route.maxLevel) return false;
      }
      return true;
    }),
    [currentUser]
  );

  const toggleMenu = (key: string) => {
    setExpandedMenus(prev => ({ ...prev, [key]: !prev[key] }));
  };

  const renderContent = () => {
    switch(currentRoute) {
      case '/dashboard':
        if (currentUser.adminLevel === AdminLevel.PROVINCE) return <ProvincialDashboard />;
        if (currentUser.adminLevel === AdminLevel.CITY) return <CityDashboard />;
        return <CountyDashboard />;

      case '/institution': return <InstitutionManager currentRegionCode={selectedRegionCode} />;
      case '/resources': return <ResourceSupervision />;
      case '/services': return <ServiceSupervision />;
      case '/operations': return <OperationSupervision activeRegion={activeRegion} />;
      case '/quality': return <QualitySupervision />;
      case '/finance': return <FinancialScan currentRegionCode={selectedRegionCode} activeRegion={activeRegion} />;

      default: return <ProvincialDashboard />;
    }
  };

  const renderIcon = (iconName: string, className: string) => {
      const icons: any = {
          LayoutDashboard, Building2, Database, ScrollText, Activity, BadgeCheck, Scale, Settings,
          User, FileText, List, Bell
      };
      const Icon = icons[iconName] || LayoutDashboard;
      return <Icon className={className} />;
  };

  return (
    <div className="flex h-screen bg-gray-50 font-sans text-slate-900">

      {/* Sidebar */}
      <aside className="w-64 bg-[#1F2A38] flex flex-col shadow-lg z-20 flex-shrink-0 transition-all duration-300">
        <div className="h-16 flex items-center px-6 bg-[#17212e]">
          <div className="w-8 h-8 bg-blue-500 rounded-lg flex items-center justify-center mr-3 text-white font-bold shrink-0">省</div>
          <span className="text-white font-semibold tracking-wide text-sm truncate">省级远程医疗监管平台</span>
        </div>

        <nav className="flex-1 py-6 px-3 space-y-1 overflow-y-auto">
          {menuItems.map(item => {
            const hasChildren = item.children && item.children.length > 0;
            const isExpanded = expandedMenus[item.key];
            const isActive = currentRoute === item.path || (hasChildren && item.children?.some(c => c.path === currentRoute));

            return (
              <div key={item.key}>
                  <button
                    onClick={() => {
                        if (hasChildren) {
                            toggleMenu(item.key);
                        } else {
                            setCurrentRoute(item.path);
                        }
                    }}
                    className={`w-full flex items-center px-3 py-2.5 text-sm font-medium rounded-md transition-all duration-200 group
                      ${isActive && !hasChildren
                        ? 'bg-blue-600 text-white shadow-md' 
                        : 'text-gray-400 hover:bg-[#2A3B4D] hover:text-white'
                      }`}
                  >
                    {renderIcon(item.icon, `mr-3 h-5 w-5 shrink-0 ${isActive ? 'text-white' : 'text-gray-500 group-hover:text-white'}`)}
                    <span className="truncate flex-1 text-left">{item.label}</span>
                    {hasChildren && (
                        isExpanded ? <ChevronDown className="w-4 h-4 ml-2" /> : <ChevronRight className="w-4 h-4 ml-2" />
                    )}
                  </button>

                  {/* Nested Sub-menu */}
                  {hasChildren && isExpanded && (
                      <div className="ml-4 pl-3 border-l border-gray-700 mt-1 space-y-1">
                          {item.children?.map(sub => (
                              <button
                                key={sub.key}
                                onClick={() => setCurrentRoute(sub.path)}
                                className={`w-full flex items-center px-3 py-2 text-sm rounded-md transition-colors
                                  ${currentRoute === sub.path 
                                    ? 'text-blue-400 bg-[#2A3B4D]' 
                                    : 'text-gray-500 hover:text-gray-300'
                                  }`}
                              >
                                {renderIcon(sub.icon, "w-4 h-4 mr-2 opacity-70")}
                                {sub.label}
                              </button>
                          ))}
                      </div>
                  )}
              </div>
            );
          })}
        </nav>

        {/* User Profile / Role Switcher */}
        <div className="p-4 bg-[#17212e] border-t border-gray-700">
          <div className="flex items-center gap-3 mb-4">
            <div className="w-8 h-8 rounded-full bg-gray-500 flex items-center justify-center text-xs text-white shrink-0">
              {currentUser.name[0]}
            </div>
            <div className="flex-1 min-w-0">
              <p className="text-sm font-medium text-white truncate">{currentUser.name}</p>
              <p className="text-xs text-gray-400 truncate">{currentUser.role}</p>
            </div>
          </div>
          <div className="grid grid-cols-3 gap-1">
             <button onClick={() => setCurrentUser(MOCK_USERS.provinceAdmin)} className="text-[10px] bg-gray-700 text-gray-300 p-1 rounded hover:bg-gray-600 transition-colors" title="切换至省级视角">省级</button>
             <button onClick={() => setCurrentUser(MOCK_USERS.cityAdmin)} className="text-[10px] bg-gray-700 text-gray-300 p-1 rounded hover:bg-gray-600 transition-colors" title="切换至市级视角">市级</button>
             <button onClick={() => setCurrentUser(MOCK_USERS.countyAdmin)} className="text-[10px] bg-gray-700 text-gray-300 p-1 rounded hover:bg-gray-600 transition-colors" title="切换至县级视角">县级</button>
          </div>
        </div>
      </aside>

      {/* Main Content */}
      <div className="flex-1 flex flex-col min-w-0 overflow-hidden">

        {/* Header */}
        <header className="bg-white shadow-sm h-16 flex items-center justify-between px-8 z-10 border-b border-gray-200 shrink-0">
          <div className="flex items-center gap-4 flex-1 min-w-0">
            <h1 className="text-lg font-bold text-gray-800 tracking-tight truncate">
               {currentRoute.includes('/system/') 
                 ? ROUTE_CONFIG.find(r => r.key === 'system')?.children?.find(c => c.path === currentRoute)?.label 
                 : menuItems.find(m => m.path === currentRoute)?.label || '综合监管'}
            </h1>

            <div className="hidden md:flex ml-8 items-center gap-2 bg-gray-100 rounded-full px-4 py-1.5 border border-gray-200">
               <Map className="w-4 h-4 text-gray-500" />
               <span className="text-sm font-medium text-gray-700 whitespace-nowrap">{activeRegion.name}</span>

               {subRegions.length > 0 && (
                 <>
                   <span className="text-gray-300">|</span>
                   <select 
                      className="bg-transparent text-sm text-gray-600 focus:outline-none cursor-pointer hover:text-blue-600 max-w-[120px]"
                      onChange={(e) => {
                        if (e.target.value) setSelectedRegionCode(e.target.value);
                      }}
                      value=""
                   >
                     <option value="" disabled>下钻区域...</option>
                     {subRegions.map(r => (
                       <option key={r.code} value={r.code}>{r.name}</option>
                     ))}
                   </select>
                 </>
               )}

               {activeRegion.code !== currentUser.regionCode && (
                 <button 
                   onClick={() => setSelectedRegionCode(currentUser.regionCode)}
                   className="ml-2 text-xs text-blue-600 hover:underline whitespace-nowrap"
                 >
                   重置
                 </button>
               )}
            </div>
          </div>

          <div className="flex items-center gap-4 shrink-0">
             <div className="relative">
                <Bell className="w-5 h-5 text-gray-500 cursor-pointer hover:text-gray-700" />
                <span className="absolute -top-1 -right-1 w-2 h-2 bg-red-500 rounded-full"></span>
             </div>
             <div className="h-6 w-px bg-gray-200"></div>
             <Button variant="ghost" size="sm">
                <LogOut className="w-4 h-4 mr-2" />
                退出登录
             </Button>
          </div>
        </header>

        {/* Page Content */}
        <main className="flex-1 overflow-y-auto p-6 scroll-smooth">
          <div className="max-w-7xl mx-auto h-full">
            {renderContent()}
          </div>
        </main>
      </div>
    </div>
  );
}

import React, { useState, useMemo } from 'react';
import { MOCK_DOCTORS, MOCK_OPERATIONS, MOCK_INSTITUTIONS, MOCK_REGIONS, MOCK_PATIENTS, H2H_SERVICES, TOC_SERVICES } from '../constants';
import { Table, Badge, Button, Modal } from './UI';
import { Region, Doctor, Patient, AdminLevel, OperationStat, Institution } from '../types';
import { 
  Users, Stethoscope, Star, Activity, BarChart2, 
  Search, MessageSquareWarning, ClipboardList, Download,
  Eye, Edit, FileText, Filter, Calendar, MapPin, UserSquare, ShieldCheck,
  Building2, Trophy, ArrowRightLeft, Percent, CheckCircle, TrendingUp,
  LayoutGrid, List, Wifi, Network, ChevronDown, ChevronUp, ExternalLink
} from 'lucide-react';

// --- Shared: Stat Card ---
const StatCard = ({ label, value, subtext, icon: Icon, color = "blue" }: any) => (
  <div className={`bg-white p-4 rounded-lg border border-gray-200 shadow-sm flex items-center justify-between`}>
     <div>
        <div className="text-gray-500 text-xs font-medium">{label}</div>
        <div className={`text-2xl font-bold text-${color}-700 mt-1`}>{value}</div>
        {subtext && <div className="text-[10px] text-gray-400 mt-1">{subtext}</div>}
     </div>
     <div className={`p-2.5 bg-${color}-50 rounded-full text-${color}-600`}>
        <Icon className="w-5 h-5" />
     </div>
  </div>
);

// --- Shared: Interactive Ranking Chart ---
const InteractiveRankingChart = ({ 
  data, 
  onBarClick, 
  activeId, 
  color = "blue", 
  title,
  unit = "人"
}: { 
  data: { id: string; name: string; value: number }[]; 
  onBarClick: (id: string) => void; 
  activeId: string;
  color?: string;
  title: string;
  unit?: string;
}) => {
  const maxVal = Math.max(...data.map(d => d.value)) || 1;

  const getColorClasses = (isActive: boolean) => {
    if (isActive) {
      if (color === 'blue') return 'bg-blue-600';
      if (color === 'green') return 'bg-green-600';
      return 'bg-blue-600';
    }
    if (color === 'blue') return 'bg-blue-400 group-hover:bg-blue-500';
    if (color === 'green') return 'bg-green-400 group-hover:bg-green-500';
    return 'bg-blue-400 group-hover:bg-blue-500';
  };

  return (
    <div className="bg-white p-5 rounded-lg border border-gray-200 shadow-sm h-full flex flex-col">
      <div className="flex justify-between items-center mb-4 shrink-0">
        <h3 className="font-bold text-gray-800 flex items-center gap-2">
           <Trophy className={`w-4 h-4 text-${color}-600`} />
           {title}
        </h3>
        <span className="text-[10px] text-gray-400 bg-gray-50 px-2 py-0.5 rounded border border-gray-100">
            Top 10
        </span>
      </div>
      <div className="flex-1 overflow-y-auto pr-2 space-y-3 custom-scrollbar">
        {data.length > 0 ? (
          data.map((item, index) => {
             const isActive = activeId === item.id;
             const percent = (item.value / maxVal) * 100;
             return (
               <div 
                 key={item.id} 
                 className={`group cursor-pointer transition-all ${isActive ? 'opacity-100' : 'opacity-80 hover:opacity-100'}`}
                 onClick={() => onBarClick(item.id)}
               >
                 <div className="flex justify-between text-xs mb-1">
                    <div className="flex items-center gap-2 overflow-hidden">
                       <span className={`flex items-center justify-center w-4 h-4 rounded text-[10px] font-bold shrink-0 
                         ${index < 3 ? `bg-${color}-100 text-${color}-700` : 'bg-gray-100 text-gray-500'}
                       `}>
                         {index + 1}
                       </span>
                       <span className={`truncate font-medium ${isActive ? `text-${color}-700 font-bold` : 'text-gray-700'}`} title={item.name}>
                         {item.name}
                       </span>
                    </div>
                    <span className="font-mono font-bold text-gray-900">{item.value} <span className="text-[10px] font-normal text-gray-400">{unit}</span></span>
                 </div>
                 <div className="w-full h-2 bg-gray-100 rounded-full overflow-hidden">
                    <div 
                      className={`h-full rounded-full transition-all duration-500 ${getColorClasses(isActive)}`}
                      style={{ width: `${percent}%` }}
                    />
                 </div>
               </div>
             );
          })
        ) : (
          <div className="h-full flex items-center justify-center text-gray-400 text-sm">暂无排名数据</div>
        )}
      </div>
    </div>
  );
};

// --- 5. Service Supervision (Optimized) ---
export const ServiceSupervision = () => {
  const [activeTab, setActiveTab] = useState<'stats' | 'details'>('stats');

  // Region Filters
  const [filterRegion, setFilterRegion] = useState({ province: '520000', city: '', county: '' });

  const provinces = MOCK_REGIONS.filter(r => r.level === AdminLevel.PROVINCE);
  const cities = useMemo(() => MOCK_REGIONS.filter(r => r.parentCode === filterRegion.province), [filterRegion.province]);
  const counties = useMemo(() => filterRegion.city ? MOCK_REGIONS.filter(r => r.parentCode === filterRegion.city) : [], [filterRegion.city]);

  const handleRegionChange = (key: 'province' | 'city' | 'county', val: string) => {
      setFilterRegion(prev => {
          const next = { ...prev, [key]: val };
          if (key === 'province') { next.city = ''; next.county = ''; }
          if (key === 'city') { next.county = ''; }
          return next;
      });
  };

  const currentRegionCode = filterRegion.county || filterRegion.city || filterRegion.province;

  // Filtered Institutions based on Region
  const filteredInsts = useMemo(() => {
    return MOCK_INSTITUTIONS.filter(inst => inst.regionCode.startsWith(currentRegionCode));
  }, [currentRegionCode]);

  // --- STATS VIEW LOGIC ---
  const [selectedServiceForModal, setSelectedServiceForModal] = useState<{name: string, type: string} | null>(null);

  // Generate Service Stats: Which services are most popular?
  const serviceStatsData = useMemo(() => {
     const generateStats = (services: string[], type: string) => services.map((s, i) => {
        // Deterministic mock using char codes for stability
        const seed = s.charCodeAt(0) + filteredInsts.length; 
        const instCount = Math.ceil(filteredInsts.length * ((seed % 80 + 20) / 100)); // 20-100% coverage mock
        const docCount = instCount * (seed % 5 + 2);

        return {
           id: `${type}-${i}`,
           name: s,
           type: type,
           instCount: instCount, // For ranking Services by Hospital Count
           docCount: docCount,   // For ranking Services by Doctor Count
           totalVolume: instCount * (seed % 50 + 100)
        };
     }).sort((a, b) => b.instCount - a.instCount); // Default sort by popularity

     return {
        h2h: generateStats(H2H_SERVICES, 'H2H'),
        toc: generateStats(TOC_SERVICES, 'ToC')
     };
  }, [filteredInsts]);

  // --- MODAL LOGIC: Service Detail Drill-down ---
  // When clicking a service, show Top 10 Hospitals and List of All Hospitals for that service
  const serviceDetailData = useMemo(() => {
      if (!selectedServiceForModal) return null;

      // Mock: Generate specific stats for hospitals providing this service
      // In real app, query by serviceId
      const seed = selectedServiceForModal.name.charCodeAt(0);

      const hospitals = filteredInsts.map((inst, i) => {
          const hasService = (seed + i) % 3 !== 0; // Randomly assign service availability
          if (!hasService) return null;

          const volume = Math.floor(Math.random() * 1000) + 50;
          const doctors = Math.floor(Math.random() * 20) + 2;

          return {
              ...inst,
              serviceStats: { volume, doctors }
          };
      }).filter(Boolean) as (Institution & { serviceStats: { volume: number, doctors: number } })[];

      // Sort by volume for Top 10
      const sortedByVolume = [...hospitals].sort((a, b) => b.serviceStats.volume - a.serviceStats.volume);

      return {
          hospitals,
          top10: sortedByVolume.slice(0, 10).map(h => ({
              id: h.id,
              name: h.name,
              value: h.serviceStats.volume
          }))
      };

