// --- 6. Operation Supervision ---
export const OperationSupervision = ({ activeRegion }: { activeRegion: Region }) => {
  // ... (Code remains largely the same, included for context or minor updates if necessary, but skipping redundant paste for brevity as requested change was primarily in Service/Quality)
  // Region Filter State
  const [filterRegion, setFilterRegion] = useState({ 
      province: '520000', 
      city: '', 
      county: '' 
  });

  // Sync with global activeRegion initially
  useEffect(() => {
    const level = activeRegion.level;
    if (level === AdminLevel.PROVINCE) {
         setFilterRegion({ province: activeRegion.code, city: '', county: '' });
    } else if (level === AdminLevel.CITY) {
         setFilterRegion({ province: activeRegion.parentCode || '520000', city: activeRegion.code, county: '' });
    } else if (level === AdminLevel.COUNTY) {
         setFilterRegion({ province: '520000', city: activeRegion.parentCode || '', county: activeRegion.code });
    }
  }, [activeRegion]);

  // Options for Selects
  const provinces = MOCK_REGIONS.filter(r => r.level === AdminLevel.PROVINCE);
  const cities = useMemo(() => MOCK_REGIONS.filter(r => r.parentCode === filterRegion.province), [filterRegion.province]);
  const counties = useMemo(() => filterRegion.city ? MOCK_REGIONS.filter(r => r.parentCode === filterRegion.city) : [], [filterRegion.city]);

  const handleRegionChange = (key: 'province' | 'city' | 'county', val: string) => {
      setFilterRegion(prev => {
          const next = { ...prev, [key]: val };
          if (key === 'province') { next.city = ''; next.county = ''; }
          if (key === 'city') { next.county = ''; }
          return next;
      });
  };

  // Determine current effective level code
  const currentLevelCode = filterRegion.county || filterRegion.city || filterRegion.province;

  // Find data for *sub-regions* of the selected code
  const operationalData = useMemo(() => {
      // Find direct children in operations mock
      let data = MOCK_OPERATIONS.filter(op => op.parentCode === currentLevelCode);

      // Fallback: if no children found (leaf level or data missing in mock), show the node itself
      if (data.length === 0) {
          data = MOCK_OPERATIONS.filter(op => op.regionCode === currentLevelCode);
      }
      return data.sort((a, b) => b.totalWorkload - a.totalWorkload);
  }, [currentLevelCode]);

  // KPI Aggregation
  const stats = useMemo(() => {
      return operationalData.reduce((acc, curr) => ({
          total: acc.total + curr.totalWorkload,
          tele: acc.tele + curr.telemedicineCount,
          net: acc.net + curr.internetMedCount
      }), { total: 0, tele: 0, net: 0 });
  }, [operationalData]);

  // Drill down handler for Chart
  const handleDrillDown = (code: string) => {
      const region = MOCK_REGIONS.find(r => r.code === code);
      if (!region) return;

      if (region.level === AdminLevel.CITY) {
          setFilterRegion(prev => ({ ...prev, city: code, county: '' }));
      } else if (region.level === AdminLevel.COUNTY) {
          setFilterRegion(prev => ({ ...prev, county: code }));
      }
  };

  return (
    <div className="space-y-6 animate-in fade-in duration-300">
       {/* 1. Filter Bar */}
       <div className="bg-white p-3 rounded-lg border border-gray-200 shadow-sm flex flex-wrap gap-4 items-center">
            <div className="flex items-center gap-2 text-sm text-gray-600">
                <Filter className="w-4 h-4" />
                <span className="font-bold">行政区域筛选:</span>
            </div>
            <select 
                className="border border-gray-300 rounded px-2 py-1 text-sm bg-gray-50"
                value={filterRegion.province}
                onChange={(e) => handleRegionChange('province', e.target.value)}
                disabled
            >
                {provinces.map(r => <option key={r.code} value={r.code}>{r.name}</option>)}
            </select>
            <select 
                className="border border-gray-300 rounded px-2 py-1 text-sm bg-gray-50"
                value={filterRegion.city}
                onChange={(e) => handleRegionChange('city', e.target.value)}
            >
                <option value="">全部城市</option>
                {cities.map(r => <option key={r.code} value={r.code}>{r.name}</option>)}
            </select>
            <select 
                className="border border-gray-300 rounded px-2 py-1 text-sm bg-gray-50"
                value={filterRegion.county}
                onChange={(e) => handleRegionChange('county', e.target.value)}
                disabled={!filterRegion.city}
            >
                <option value="">全部区县</option>
                {counties.map(r => <option key={r.code} value={r.code}>{r.name}</option>)}
            </select>
            <div className="ml-auto text-xs text-gray-400">
                当前范围包含 {operationalData.length} 个子区域
            </div>
       </div>

       {/* 2. KPI Cards */}
       <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
          <StatCard label="区域业务总量" value={stats.total.toLocaleString()} subtext="本月累计 (人次)" icon={Activity} color="blue" />
          <StatCard label="远程医疗业务量" value={stats.tele.toLocaleString()} subtext="机构间协作 (H2H)" icon={Network} color="indigo" />
          <StatCard label="互联网医疗业务量" value={stats.net.toLocaleString()} subtext="面向患者 (ToC)" icon={Wifi} color="green" />
       </div>

       {/* 3. Distribution Chart & Table */}
       <div className="grid grid-cols-1 lg:grid-cols-3 gap-6 h-[500px]">
           {/* Chart */}
           <div className="lg:col-span-1 h-full">
               <InteractiveRankingChart 
                    title="下辖区域业务量排名"
                    data={operationalData.map(d => ({ id: d.regionCode, name: d.regionName, value: d.totalWorkload }))}
                    activeId=""
                    onBarClick={handleDrillDown}
                    color="blue"
                    unit="人次"
               />
           </div>

           {/* Table */}
           <div className="lg:col-span-2 bg-white border border-gray-200 rounded-lg overflow-hidden flex flex-col">
                <div className="px-6 py-4 border-b border-gray-200 bg-gray-50 flex justify-between items-center shrink-0">
                    <h3 className="font-bold text-gray-800">区域运行数据明细</h3>
                    <Button size="sm" variant="ghost"><Download className="w-4 h-4 mr-1"/> 导出数据</Button>
                </div>
                <div className="flex-1 overflow-auto">
                    <Table<OperationStat>
                        rowKey="regionCode"
                        dataSource={operationalData}
                        columns={[
                            { title: '区域名称', render: (r) => <span className="font-bold text-gray-800">{r.regionName}</span> },
                            { title: '远程医疗量', render: (r) => r.telemedicineCount.toLocaleString() },
                            { title: '互联网医疗量', render: (r) => r.internetMedCount.toLocaleString() },
                            { title: '总业务负荷', render: (r) => <span className="text-blue-600 font-bold">{r.totalWorkload.toLocaleString()}</span> },
                            { title: '环比增长', render: () => <span className="text-red-500 text-xs flex items-center"><TrendingUp className="w-3 h-3 mr-1"/> +5.2%</span> },
                            { title: '占比', render: (r) => {
                                const percent = stats.total > 0 ? (r.totalWorkload / stats.total * 100).toFixed(1) : 0;
                                return <span className="text-xs text-gray-500">{percent}%</span>
                            }}
                        ]}
                    />
                </div>
           </div>
       </div>
    </div>
  );
};

