     const seed = institution.id.charCodeAt(0);
     const services = [...H2H_SERVICES.slice(0, 5), ...TOC_SERVICES.slice(0, 4)];

     return services.map((svc, idx) => {
        const count = Math.floor((seed * (idx + 1) * 13) % 500) + 12;
        const price = Math.floor((seed * (idx + 1) * 7) % 300) + 50;
        return {
           id: `rpt-${idx}`,
           serviceName: svc,
           category: getServiceCategory(svc),
           count: count,
           price: price,
           total: count * price
        };
     }).sort((a, b) => b.total - a.total);
  }, [institution]);

  const totalIncome = financialReport.reduce((acc, cur) => acc + cur.total, 0);

  return (
    <div className="space-y-6 animate-in slide-in-from-right duration-300">
