export const FinancialScan: React.FC<Props> = ({ activeRegion }) => {
  const [selectedInstitution, setSelectedInstitution] = useState<Institution | null>(null);
  const [filters, setFilters] = useState({
      category: 'All' as 'All' | 'H2H' | 'ToC',
      dateRange: 'month'
  });

  // --- Mock Aggregated Data Generation (Simulating Backend Aggregation) ---
  const aggregatedStats = useMemo(() => {
      // Base multiplier based on region level for realistic numbers
      const multiplier = activeRegion.level === AdminLevel.PROVINCE ? 100 : activeRegion.level === AdminLevel.CITY ? 20 : 5;

      const totalIncome = 12584000 * multiplier * 0.8;
      const totalVolume = 45200 * multiplier * 0.9;
      const instCount = MOCK_INSTITUTIONS.filter(i => i.regionCode.startsWith(activeRegion.code)).length * (activeRegion.level === AdminLevel.PROVINCE ? 15 : 1);

      return {
          totalIncome,
          totalVolume,
          avgFee: Math.floor(totalIncome / totalVolume),
          instCount,
          h2hIncome: totalIncome * 0.65,
          tocIncome: totalIncome * 0.35,
          trend: 12.5
      };
  }, [activeRegion]);

  // --- Sub-region List Logic ---
  const subRegionStats = useMemo(() => {
     // Find direct children
     const children = MOCK_REGIONS.filter(r => r.parentCode === activeRegion.code);
     // Fallback if no children (leaf node), show itself
     const list = children.length > 0 ? children : [activeRegion];

     return list.map(r => {
        // Mock random stats per sub-region
        const seed = r.code.charCodeAt(r.code.length - 1);
        const volume = Math.floor(aggregatedStats.totalVolume / list.length * (0.8 + (seed % 5)/10));
        const income = Math.floor(aggregatedStats.totalIncome / list.length * (0.8 + (seed % 5)/10));

        return {
            ...r,
            income,
            volume,
            h2hIncome: income * 0.6,
            tocIncome: income * 0.4
        };
     }).sort((a, b) => b.income - a.income);
  }, [activeRegion, aggregatedStats]);

  // --- Institution List Logic (Filtered) ---
