               <StatCard label="资质合格率" value={doctorStats.passRate} subtext="执业证/资格证" icon={ShieldCheck} color="green" />
               <StatCard label="高级职称占比" value={doctorStats.seniorRatio} subtext="副高及以上" icon={Star} color="indigo" />
               <StatCard label="人均服务量" value={doctorStats.avgService} subtext="近12个月" icon={Activity} color="orange" />
            </div>

            {/* Middle Row: Charts (Left: Top 5, Right: Distribution) */}
            <div className="grid grid-cols-1 lg:grid-cols-12 gap-6 h-[260px]">
               {/* Left: Ranking Chart (Top 5) */}
               <div className="lg:col-span-5 h-full">
                  <InteractiveRankingChart 
                     title="医院医生资源 TOP5" 
                     data={doctorRankingData} 
                     activeId={filters.hospitalId}
                     onBarClick={handleHospitalBarClick}
                     color="blue"
                  />
               </div>

               {/* Right: Distribution */}
               <div className="lg:col-span-7 bg-white p-5 rounded-lg border border-gray-200 shadow-sm flex flex-col justify-center h-full">
                  <div className="flex justify-between items-center mb-4">
                     <div className="text-sm font-bold text-gray-800">职称分布统计</div>
                     <Badge type="neutral">全省均值对比</Badge>
                  </div>
                  <div className="flex items-end gap-3 h-full pb-2">
                     {Object.entries(doctorStats.titles).map(([title, count]) => {
                        const height = doctorStats.total > 0 ? Math.max(((count as number) / doctorStats.total) * 100, 10) : 0;
                        return (
                           <div key={title} className="flex-1 flex flex-col items-center gap-2 group h-full justify-end">
                              <div className="text-sm font-bold text-gray-700">{count as number}</div>
                              <div className="w-full bg-indigo-50 rounded-t-sm relative overflow-hidden" style={{ height: '100%' }}>
                                 <div className="absolute bottom-0 left-0 right-0 bg-indigo-500 group-hover:bg-indigo-600 transition-all rounded-t-sm" style={{ height: `${height}%` }}></div>
                              </div>
                              <div className="text-[10px] text-gray-500 truncate w-full text-center" title={title}>{title}</div>
                           </div>
                        )
                     })}
                  </div>
               </div>
            </div>

            {/* Bottom Row: List */}
            <div className="bg-white border border-gray-200 rounded-lg overflow-hidden">
                <div className="px-6 py-4 border-b border-gray-200 bg-gray-50 flex justify-between items-center">
                    <div>
                      <h3 className="font-bold text-gray-800">注册医师资源名录</h3>
