          { name: '普外科', count: 10 },
          { name: '儿科', count: 6 },
          { name: '皮肤科', count: 4 },
      ].sort((a, b) => b.count - a.count);

      return {
          h2h: activeH2H,
          toc: activeToC,
          depts: depts
      };
  }, [selectedHospitalDetail]);


