            color="orange"
         />
         <KpiCard 
            title="参与结算机构" 
            value={aggregatedStats.instCount} 
            subText="家医疗机构"
            trend={0}
            icon={Building2}
            color="indigo"
         />
      </div>

      {/* 3. Tier 2: Breakdown Analysis */}
      <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
